CREATE FUNCTION add_key(table_name text, key_name text, keys text) RETURNS void
    LANGUAGE plpgsql
    AS $$
    DECLARE
      index_name varchar;
    BEGIN
       index_name = table_name || '_' || key_name;
       IF NOT EXISTS (
           SELECT 1
           FROM   pg_class c
           JOIN   pg_namespace n ON n.oid = c.relnamespace
           WHERE  lower(c.relname) = lower(index_name)
             AND  n.nspname = 'sg'
           ) THEN
       
           EXECUTE 'CREATE INDEX ' || index_name || ' ON ' || table_name || ' ' || keys;
       END IF;
    END;
  $$;


