-- Name: check_password(character varying, text); Type: FUNCTION; Schema: sg; Owner: servergraph
--

CREATE FUNCTION check_password(username character varying, passwd text) RETURNS smallint
    LANGUAGE plpgsql
    AS $$
  DECLARE
    salt VARCHAR(2) DEFAULT NULL;
    pwd_hash VARCHAR(32) DEFAULT NULL;
    crypt_res VARCHAR(32) DEFAULT NULL;
    have_crypt VARCHAR(5);
  BEGIN
    SELECT u.password INTO pwd_hash FROM Users u, Symbols s WHERE s.id = u.id AND s.name = username;

    IF pwd_hash IS NULL THEN
      RETURN 1; /* no such user */
    ELSE
      /* after password encryption salt is stored in first two chars */
      /* of the encrypted password */
      salt := LEFT(pwd_hash, 2);

      /* Use pgcrypto.crypt function */
      crypt_res := crypt(passwd, salt);

      IF crypt_res <> pwd_hash THEN
        RETURN 2; /* incorrect password */
      ELSE
        RETURN 0; /* auth successful */
      END IF;
    END IF;
    RETURN 1;
  END;
  $$;


ALTER FUNCTION sg.check_password(username character varying, passwd text) OWNER TO servergraph;

