    LANGUAGE plpgsql
    AS $$
    DECLARE ng_view_name VARCHAR(128);
    DECLARE statement VARCHAR(512);
    BEGIN
        -- public_tsm_node_history_sas need to be converted to public_ng_tsm_nodes_history_sas
        IF ( view_name = 'public_tsm_node_history_sas' )
        THEN
          ng_view_name = 'public_ng_tsm_nodes_history_sas';
        ELSE
