            SELECT baseview.*, ngs.ng_id,ngs.ng_name FROM ' || view_name || ' 
            AS baseview
            JOIN ngs_view AS ngs
              ON ( baseview.node_id = ngs.ng_member_id and baseview.user_id=ngs.user_id ) ' ;

    END;
  $$;


