CREATE FUNCTION delete_data_for_server(servername character varying) RETURNS void
    LANGUAGE plpgsql
    AS $$
  DECLARE table_name VARCHAR(256)  DEFAULT '';
  DECLARE serverID       INT          DEFAULT 0;
  DECLARE   curs_1        refcursor;
  DECLARE   curs_2        refcursor;
  DECLARE   curs_3        refcursor;
  DECLARE   curs_4        refcursor;
  BEGIN
  
    SELECT id INTO serverID FROM Servers WHERE name = servername;
  
    -- update base tables
    DELETE FROM Symbols WHERE id =serverID;
    DELETE FROM Servers WHERE id = serverID;
    EXECUTE 'DELETE FROM data_set WHERE subtype=''' || servername || '''';
 
    -- get the table that contains server_id column
    OPEN curs_1 SCROLL FOR SELECT c.TABLE_NAME FROM INFORMATION_SCHEMA.COLUMNS c JOIN INFORMATION_SCHEMA.TABLES t ON (t.TABLE_NAME=c.TABLE_NAME  AND t.TABLE_SCHEMA=c.TABLE_SCHEMA) WHERE c.TABLE_SCHEMA='sg' AND c.COLUMN_NAME = 'server_id' AND t.TABLE_TYPE='BASE TABLE';

    LOOP
        FETCH curs_1 INTO table_name;
        EXIT WHEN NOT FOUND;
        EXECUTE 'DELETE FROM ' || table_name || ' WHERE server_id=' || serverID;
    END LOOP;
    CLOSE curs_1;

    -- get the table that contains backupserverid column
    OPEN curs_2 SCROLL FOR SELECT c.TABLE_NAME FROM INFORMATION_SCHEMA.COLUMNS c JOIN INFORMATION_SCHEMA.TABLES t ON (t.TABLE_NAME=c.TABLE_NAME  AND t.TABLE_SCHEMA=c.TABLE_SCHEMA) WHERE c.TABLE_SCHEMA='sg' AND c.COLUMN_NAME LIKE 'backupserverid' AND t.TABLE_TYPE='BASE TABLE';

    LOOP
        FETCH curs_2 INTO table_name;
        EXIT WHEN NOT FOUND;
        EXECUTE 'DELETE FROM ' || table_name || ' WHERE backupserverid=' || serverID;
    END LOOP;
    CLOSE curs_2;

    -- get the table that contains server_name column
    OPEN curs_3 SCROLL FOR SELECT c.TABLE_NAME FROM INFORMATION_SCHEMA.COLUMNS c JOIN INFORMATION_SCHEMA.TABLES t ON (t.TABLE_NAME=c.TABLE_NAME  AND t.TABLE_SCHEMA=c.TABLE_SCHEMA) WHERE c.TABLE_SCHEMA='sg' AND c.COLUMN_NAME LIKE 'server_name' AND t.TABLE_TYPE='BASE TABLE';

    LOOP
        FETCH curs_3 INTO table_name;
        EXIT WHEN NOT FOUND;
        EXECUTE 'DELETE FROM ' || table_name || ' WHERE server_name=''' || servername || '''';
    END LOOP;
    CLOSE curs_3;

    -- get the table that contains servername column
    OPEN curs_4 SCROLL FOR SELECT c.TABLE_NAME FROM INFORMATION_SCHEMA.COLUMNS c JOIN INFORMATION_SCHEMA.TABLES t ON (t.TABLE_NAME=c.TABLE_NAME  AND t.TABLE_SCHEMA=c.TABLE_SCHEMA) WHERE c.TABLE_SCHEMA='sg' AND c.COLUMN_NAME LIKE 'servername' AND t.TABLE_TYPE='BASE TABLE';

    LOOP
        FETCH curs_4 INTO table_name;
        EXIT WHEN NOT FOUND;
        EXECUTE 'DELETE FROM ' || table_name || ' WHERE servername=''' || servername || '''';
    END LOOP;
    CLOSE curs_4;
  END;
  $$;


