CREATE FUNCTION delete_orphan_data() RETURNS void
    LANGUAGE plpgsql
    AS $$
    DECLARE raw_table_name  VARCHAR(200)  DEFAULT '';
    DECLARE private_table_name  VARCHAR(200)  DEFAULT '';
    DECLARE cur CURSOR FOR SELECT TABLE_NAME FROM INFORMATION_SCHEMA.TABLES WHERE table_name like 'raw_tsm%' or table_name like 'raw_avamar%';
    
    BEGIN
      OPEN cur;
      LOOP
        FETCH cur INTO raw_table_name;
        EXIT WHEN NOT FOUND;
        EXECUTE 'DELETE FROM ' || raw_table_name || ' WHERE SERVER_NAME NOT IN ( SELECT S.name FROM Symbols S, Servers srv WHERE S.id=srv.id AND S.name !=''site'')';
        private_table_name=replace(raw_table_name,'raw','private');
        IF EXISTS (
           SELECT TABLE_NAME FROM INFORMATION_SCHEMA.TABLES WHERE table_name=private_table_name
        )
        THEN
          EXECUTE 'DELETE FROM ' || private_table_name || ' WHERE SERVER_NAME NOT IN ( SELECT S.name FROM Symbols S, Servers srv WHERE S.id=srv.id AND S.name !=''site'')';
