-- Name: get_default_scene(integer, character varying, text, integer); Type: FUNCTION; Schema: sg; Owner: servergraph
--

CREATE FUNCTION get_default_scene(atmpid integer, asectionname character varying, aservertype text, auserid integer) RETURNS SETOF get_default_scene_t
    LANGUAGE plpgsql
    AS $$
  DECLARE id            INT   DEFAULT    -1;
  DECLARE id1           INT   DEFAULT    -1;
  DECLARE id2           INT   DEFAULT    -1;
  DECLARE defaultName   TEXT  DEFAULT    '';
  DECLARE	defaultType   TEXT  DEFAULT    '';
  DECLARE UserPreference_defaultName VARCHAR(1000) DEFAULT '';
  DECLARE curs          refcursor;
  DECLARE row           get_default_scene_t%ROWTYPE  ;

  BEGIN
    SELECT shuttle_id   into id  FROM tmp_shuttle_menu  WHERE tmp_id = aTmpID AND parent_id = 0;
    SELECT shuttle_id   into id1 FROM tmp_shuttle_menu  WHERE tmp_id = aTmpID AND parent_id = id  AND "type" ILIKE aSectionName;
    SELECT shuttle_id   into id2 FROM tmp_shuttle_menu  WHERE tmp_id = aTmpID AND parent_id = id1 AND name   ILIKE aServerType;

    select default_scene_name INTO defaultName /* define the default scene */
    from (
      SELECT path as default_scene_name, '0' as pr       /* check the trunk of navigation tree */
      FROM tmp_shuttle_menu
      WHERE tmp_id = aTmpID	AND (parent_id = id1 OR parent_id = id2) AND path IS NOT NULL AND visible=2

      UNION
      (
      SELECT path as default_scene_name, '1' as pr       /* check the first branch of navigation tree */
      FROM tmp_shuttle_menu
      WHERE tmp_id = aTmpID
      AND parent_id in 
        (
        SELECT shuttle_id
        FROM tmp_shuttle_menu
        WHERE tmp_id = aTmpID 
          AND parent_id = id2 
          AND path IS NULL
        ) 
      AND path IS NOT NULL AND visible=2
      )

      UNION
      (
      SELECT path as default_scene_name, '2' as pr       /* check the second branch of navigation tree */
      FROM tmp_shuttle_menu
      WHERE tmp_id = aTmpID
        AND parent_id in
          (
          SELECT shuttle_id
          FROM tmp_shuttle_menu
          WHERE tmp_id = aTmpID
            AND parent_id in 
              (
              SELECT shuttle_id
              FROM tmp_shuttle_menu
              WHERE tmp_id = aTmpID 
                AND parent_id = id2 
                AND path IS NULL
              ) 
            AND path IS NULL 
          )
        AND path IS NOT NULL AND visible=2
      )
     ORDER BY pr, default_scene_name
     limit 1
    ) as temp ;

    -- define the default scene from user_preferences:
    CASE 
      WHEN aSectionName ILIKE 'Enterprise'       THEN defaultType := 'ent_default_report';
      WHEN aSectionName ILIKE 'Instrumentation'  THEN defaultType := 'instr_default_report';
      ELSE                                            defaultType := 'home_default_report';
    END CASE;

    SELECT setting2 into UserPreference_defaultName
    FROM user_preferences
    WHERE user_id=aUserID
      AND setting_name = defaultType
      AND ((setting1 ILIKE aServerType and aSectionName ILIKE 'Enterprise') or (setting1 = ' ' and aSectionName NOT ILIKE 'Enterprise'))
    ;

    OPEN curs SCROLL FOR
      SELECT path as default_scene_name, '0' as pr      -- check the existence of default scene from user_preferences in tmp_shuttle_menu (in case of its absence select default scene)
      FROM tmp_shuttle_menu
      WHERE 1=1
        AND path=UserPreference_defaultName
        AND tmp_id=aTmpID

      UNION
      (
      SELECT path as default_scene_name, '1' as pr      -- check the default scene existence in tmp_shuttle_menu (in case of its absence select the first select the first found scene)
      FROM tmp_shuttle_menu
      WHERE 1=1
        AND path=defaultName
        AND tmp_id=aTmpID
      )

      UNION
      (
      SELECT * FROM (
        SELECT path as default_scene_name, '2' as pr       -- check the trunk of navigation tree
        FROM tmp_shuttle_menu
        WHERE tmp_id = aTmpID	AND parent_id = id2 AND path IS NOT NULL

        UNION
        (
        SELECT path as default_scene_name, '3' as pr       -- check the first branch of navigation tree
        FROM tmp_shuttle_menu
        WHERE tmp_id = aTmpID
          AND parent_id in 
            (
            SELECT shuttle_id
            FROM tmp_shuttle_menu
            WHERE tmp_id = aTmpID 
              AND parent_id = id2 and path IS NULL
            ) 
          AND path IS NOT NULL
        )

        UNION
        (
        SELECT path as default_scene_name, '4' as pr       -- check the second branch of navigation tree
        FROM tmp_shuttle_menu
        WHERE tmp_id = aTmpID
          AND parent_id in
            (
            SELECT shuttle_id
            FROM tmp_shuttle_menu
            WHERE tmp_id = aTmpID
              AND parent_id in 
                (
                SELECT shuttle_id
                FROM tmp_shuttle_menu
                WHERE tmp_id = aTmpID 
                  AND parent_id = id2 and path IS NULL
                ) 
              AND path IS NULL
            )
          AND path IS NOT NULL
        )

      limit 1
      ) tmp
      )

      UNION
      SELECT 'MakeAsDefault_scn_Main' as default_scene_name, '9999' as pr
      ORDER BY pr, default_scene_name
      LIMIT 1;

    LOOP
      FETCH curs INTO row;
      EXIT WHEN NOT FOUND;
      RETURN NEXT row;
    END LOOP;
    CLOSE curs;
    RETURN;

  END;
  $$;


