--

CREATE FUNCTION get_node_groups(username character) RETURNS SETOF get_node_groups_t
    LANGUAGE plpgsql
    AS $$
  DECLARE   userid      INT     DEFAULT 0;
  DECLARE   access_id   INT     DEFAULT 0;
  DECLARE   ng_id       INT     DEFAULT 0;
  DECLARE   tmp_id      INT     DEFAULT 0;
  DECLARE   affected_row           INT             DEFAULT 0;
  DECLARE   row         node_group_list_t%ROWTYPE;
  DECLARE   curs_out            refcursor;
  DECLARE   data_out    get_node_groups_t%ROWTYPE;

  BEGIN
    SELECT id   INTO userid FROM Symbols WHERE namespace=1 AND name=username;
    SELECT s.id INTO access_id FROM Symbols s , Symbols p WHERE s.name='view' AND s.namespace=p.id AND p.name='permission' AND p.namespace=0;
    select s.id INTO ng_id FROM Symbols s WHERE s.name='node-group' AND s.namespace=0;

    -- use tmp_id to handle the multiple sessions
    SELECT FLOOR(20+RAND()*1000) INTO tmp_id;

    INSERT INTO node_group_list SELECT tmp_id as id, assign.object as ng_groups
      FROM
         Assignments assign
      WHERE
        assign.relation=access_id
        AND (assign.subject in (SELECT "group" FROM Groups WHERE member=userid) OR (assign.subject = userid) )
        AND (assign.object in ( SELECT id from NodeGroups )) GROUP BY assign.object;

    GET DIAGNOSTICS affected_row = ROW_COUNT;

    WHILE affected_row >0 LOOP
      INSERT INTO node_group_list SELECT tmp_id as id, assign.object as ng_groups
      FROM Assignments assign
      WHERE
        assign.relation=access_id
        AND (assign.subject in (SELECT "group" FROM Groups WHERE member=userid) OR (assign.subject = userid) )
        AND (assign.object in ( SELECT id from NodeGroups )) GROUP BY assign.object ON CONFLICT DO NOTHING;

        GET DIAGNOSTICS affected_row = ROW_COUNT;
    END LOOP;

    OPEN curs_out SCROLL FOR
      SELECT gl.ng_groups AS ng_groups, s.name AS ng_name FROM node_group_list gl JOIN Symbols s ON (s.id = gl.ng_groups) WHERE gl.id=tmp_id ORDER BY s.name;

     LOOP
       FETCH curs_out INTO data_out;
       EXIT WHEN NOT FOUND;
       RETURN NEXT data_out;
     END LOOP;
     CLOSE curs_out;

     -- remove the entries from the table for this session 
     DELETE FROM node_group_list WHERE id=tmp_id; 
     RETURN;

  END;
  $$;


