--

CREATE FUNCTION get_proc_ses_activity(user_date timestamp without time zone, tid integer, serverid integer) RETURNS SETOF get_proc_ses_activity_t
    LANGUAGE plpgsql
    AS $$
    DECLARE server_st VARCHAR(64);
    DECLARE user_id INT;
    DECLARE now_time TIMESTAMP;
    DECLARE start_time TIMESTAMP;
    DECLARE end_time TIMESTAMP;
  BEGIN
    EXECUTE 'CREATE TABLE IF NOT EXISTS sgdate_' || tid || '("start" TIMESTAMP, "end" TIMESTAMP)';
    EXECUTE 'TRUNCATE TABLE sgdate_' || tid;

    -- find out user id
    SELECT s.id INTO user_id FROM Symbols s, Users u WHERE u.id=s.id AND s.name=(SELECT user_name FROM tmp_shuttle_menu WHERE tmp_id=tid LIMIT 1);
    -- for testing convenience
    IF user_id IS NULL
      THEN user_id = -1;
    END IF;

    now_time := user_date;
    start_time := now_time - INTERVAL '1 DAY';
    end_time := start_time + INTERVAL '30 MINUTE';

    IF serverID = 0
    THEN server_st := '';
    ELSE server_st := 'AND t.server_id = ' || serverID;
    END IF;

    -- create 30 minute bucket
    IF user_date > NOW() THEN
      now_time := NOW();
    END IF;

    WHILE start_time < now_time LOOP
      EXECUTE 'INSERT INTO sgdate_' || tid || ' VALUES (''' || start_time || ''', ''' || end_time || ''')';
      start_time := end_time;
      end_time := end_time + INTERVAL '30 MINUTE';
    END LOOP;

    RETURN QUERY EXECUTE 'SELECT sgdate_' || tid || '.start AS "Period Start", sgdate_' || tid || '.end AS "Period End",
                    SUM(CASE WHEN t.process_type=''process'' THEN 1 ELSE 0 END) AS "Active Processes",
                    SUM(CASE WHEN t.process_type=''process'' THEN 0 ELSE 1 END) AS "Active Sessions"
             FROM (SELECT process_start, process_end, process_type
                   FROM public_tsm_process_info_sas AS t
                   WHERE
                     1=1 AND
                     t.user_id = ' || user_id || ' AND
                     (process_end > (''' || user_date || '''::TIMESTAMP - INTERVAL ''1 DAY'') OR process_end IS NULL) ' || server_st || ')
               t, sgdate_' || tid || '
             WHERE
               1=1 AND
               (t.process_end IS NULL OR t.process_end > sgdate_' || tid || '.start) AND
               t.process_start < sgdate_' || tid || '.start + INTERVAL ''30 MINUTE''
             GROUP BY 1, 2;';

    -- SELECT NOW(), NULL, NULL, NULL;

    EXECUTE 'DROP TABLE IF EXISTS sgdate_' || tid;

    RETURN;
  END;
  $$;


