--
-- TOC entry 1766 (class 1255 OID 16789)
-- Name: get_reports(character); Type: FUNCTION; Schema: sg; Owner: servergraph
--

CREATE FUNCTION get_reports(username character) RETURNS integer
    LANGUAGE plpgsql
    AS $$
  DECLARE userid INT DEFAULT 0;
  DECLARE access_id INT DEFAULT 0;
  DECLARE v_tmp_table_id INT DEFAULT 0;
  DECLARE parent_type VARCHAR(256) DEFAULT '';
  DECLARE brb_rep VARCHAR(256)  DEFAULT '';
  DECLARE tree text  DEFAULT '';
  DECLARE item text  DEFAULT '';
  DECLARE path text  DEFAULT '';
  DECLARE full_path text  DEFAULT '';
  DECLARE brb_menu_id INT DEFAULT 0;
  DECLARE parent_brb_id INT DEFAULT 0;
  DECLARE tmp_brb_id INT DEFAULT 0;
  DECLARE item_count INT DEFAULT 0;
  DECLARE brb_cur1  refcursor;
  BEGIN


      CREATE TEMPORARY TABLE IF NOT EXISTS tmp_brb_metadata_tree (
        id integer NOT NULL,
        path text NOT NULL,
        full_path text ,
        PRIMARY KEY (id)
      );

      DELETE FROM tmp_brb_metadata_tree;

      --  Get user id for the specified username */
      SELECT id INTO userid FROM Symbols WHERE namespace = 1 AND name = username;
      --  Get id for 'access' permission */
      SELECT s1.id INTO access_id FROM Symbols s1, Symbols s2 WHERE s1.name = 'access' AND s2.name = 'permission' AND s1.namespace = s2.id;
      --  Generate new session id */
      SELECT FLOOR(20+RAND()*10000) INTO v_tmp_table_id;
      --  Cleanup tmp_shuttle_menu by removing old sessions */
      DELETE FROM tmp_shuttle_menu WHERE tmp_id = v_tmp_table_id OR lastmod < (NOW() - INTERVAL '24' HOUR);
      --  create menu_lis table for each login session */
      RAISE NOTICE 'Calling vmid(%)', v_tmp_table_id ;
      EXECUTE 'CREATE TABLE IF NOT EXISTS menu_list_' || v_tmp_table_id || ' (id int, PRIMARY KEY (id))';
      EXECUTE 'CREATE TABLE IF NOT EXISTS brb_menu_list_' || v_tmp_table_id || ' (id int,type varchar(20), PRIMARY KEY (id))';
      EXECUTE 'INSERT INTO menu_list_' || v_tmp_table_id ||
      ' WITH RECURSIVE tree
      AS (
        SELECT assign.object as id FROM Assignments assign, shuttle_menu sm
             WHERE assign.relation = ' ||  access_id || ' AND (assign.subject IN ( SELECT "group" FROM Groups WHERE member = ' ||  userid || ' ) OR assign.subject = ' ||  userid ||' ) AND assign.object = sm.id
      UNION ALL
      SELECT s.id as id FROM tree AS m JOIN Symbols AS s ON (s.namespace = m.id)
      )
      SELECT id FROM tree GROUP BY id';


      EXECUTE 'INSERT INTO menu_list_' || v_tmp_table_id ||
      ' WITH RECURSIVE tree2
      AS (
        SELECT id FROM menu_list_' || v_tmp_table_id || '
       UNION ALL
       SELECT s.namespace as id FROM tree2 AS m JOIN Symbols AS s ON (s.id= m.id)
      )
      SELECT id FROM tree2 left join menu_list_' || v_tmp_table_id || ' USING(id) WHERE menu_list_' || v_tmp_table_id || '.id IS NULL GROUP BY tree2.id' ;

      --  check BRB reports */
      OPEN brb_cur1 SCROLL FOR SELECT metadata_tree FROM brb_templates WHERE public is true AND metadata_tree IS NOT NULL UNION SELECT metadata_tree FROM brb_templates b JOIN Symbols s on s.id=b.user_id WHERE s.name=username AND metadata_tree IS NOT NULL;

      LOOP
         FETCH brb_cur1 INTO brb_rep;
         EXIT WHEN NOT FOUND;

         RAISE NOTICE 'brb_rep(%)', brb_rep;
         -- keep parent type of the report, the available value is Enterprise, Home, Instrumentation , Storage
         parent_type='';
         SELECT id INTO brb_menu_id FROM Symbols WHERE name='shuttle_menu' AND namespace=0 ;
         parent_brb_id = brb_menu_id;
         RAISE NOTICE 'parent_brb_id(%)', parent_brb_id;
         -- the type of shuttle_menu is Enterprise
         EXECUTE 'INSERT INTO brb_menu_list_' ||  v_tmp_table_id || ' (id, type ) SELECT ' || brb_menu_id || ' , ''Enterprise'' WHERE NOT EXISTS ( SELECT id FROM brb_menu_list_' || v_tmp_table_id || ' WHERE id = ' || brb_menu_id || ' AND type = ''Enterprise''  ) ';

         -- split tree item and path
         SELECT SPLIT_PART(brb_rep, ':', 1), SPLIT_PART(brb_rep, ':', 2) INTO tree, path;
         full_path = tree;
