                EXECUTE 'INSERT INTO brb_menu_list_' || v_tmp_table_id || ' (id, type ) SELECT ' || tmp_brb_id || ' , ''' || parent_type || ''' WHERE NOT EXISTS ( SELECT id FROM brb_menu_list_'|| v_tmp_table_id || ' WHERE id=' || tmp_brb_id || ' AND type = ''' || parent_type || ''')' ;
            ELSE
                EXIT ;
            END IF;
            item_count= LENGTH(tree) - LENGTH(REPLACE(tree, '/', ''));
            IF (item_count= 0) THEN
                EXIT ;
            END IF;
            tree= regexp_replace(SUBSTRING(tree from '/.*'), '^\/', '');
            RAISE NOTICE 'tree_item(%)', tree;
            parent_brb_id=tmp_brb_id;
         END LOOP;
         EXECUTE 'INSERT INTO tmp_brb_metadata_tree( id, path, full_path ) SELECT ' || tmp_brb_id || ', ''' || path || ''', ''' || full_path || '''  WHERE NOT EXISTS ( SELECT id FROM tmp_brb_metadata_tree WHERE id = ' || tmp_brb_id || ')';
      END LOOP;
      CLOSE brb_cur1;


      --  Build table containing all enabled reports */
     
      EXECUTE 'DELETE FROM menu_list_' || v_tmp_table_id || ' WHERE id in ( SELECT id FROM brb_menu_list_' || v_tmp_table_id || ')';
      EXECUTE 'INSERT INTO tmp_shuttle_menu SELECT ''' || username || ''' AS user_name, ' || v_tmp_table_id || ' AS tmp_id,
         ml.id AS shuttle_id,
         sm.type,
