CREATE FUNCTION get_server_names(aservername character varying, aservertype text, auserid integer) RETURNS SETOF get_server_names_t
    LANGUAGE plpgsql
    AS $$

  DECLARE srvNameExist  INT     DEFAULT    -1;
  DECLARE nmb           INT     DEFAULT    -1;
  DECLARE row           get_server_names_t%rowtype;
  DECLARE curs           refcursor;

  BEGIN
    SELECT num into srvNameExist
    FROM (
       SELECT server_name, (row_number() OVER ( ORDER BY server_name )-1) as num
          FROM public_all_servers_sas aa
          WHERE internal_server_type ILIKE aServertype
          AND user_id = aUserid
        ) s
    WHERE server_name = aServername;

    IF srvNameExist is NULL
       THEN srvNameExist = -1;
    END IF;

    OPEN curs SCROLL FOR
      SELECT tmp0.server_id, tmp0.server_name, srvNameExist
      FROM (
        SELECT DISTINCT server_id, server_name
        FROM public_all_servers_sas
        WHERE user_id=aUserID
            AND internal_server_type ILIKE aServerType
        ORDER BY server_name ASC
        )tmp0;

    LOOP
      FETCH curs INTO row;
      EXIT WHEN NOT FOUND;

      RETURN NEXT row;
    END LOOP;
    CLOSE curs;
    RETURN;
  END;
  $$;


