CREATE FUNCTION get_server_types(asectionname character varying, aservertype character varying, atmpid integer, auserid integer) RETURNS SETOF get_server_types_t
    LANGUAGE plpgsql
    AS $$


  DECLARE section_name    TEXT;
  DECLARE id              INT     DEFAULT -1;
  DECLARE id1             INT     DEFAULT -1;
  DECLARE srvTypeExist    INT     ;
  DECLARE nmb             INT     DEFAULT -1;
  DECLARE curs            refcursor;
  DECLARE my_row          get_server_types_t%rowtype ;

  BEGIN
    SELECT shuttle_id   INTO id     FROM tmp_shuttle_menu WHERE tmp_id = aTmpID AND parent_id=0;
    SELECT shuttle_id   INTO id1    FROM tmp_shuttle_menu WHERE tmp_id = aTmpID AND parent_id=id AND name ILIKE aSectionName;
    SELECT num-1 into srvTypeExist
      FROM (
        SELECT server_type, internal_server_type, row_number() OVER () as num
        FROM public_all_servers_sas aa
        JOIN tmp_shuttle_menu sm
          ON sm.name ILIKE aa.internal_server_type
          AND sm.tmp_id = aTmpID
          AND parent_id = id1
        WHERE user_id= aUserid
          AND internal_server_type IS NOT NULL
        GROUP BY server_type, internal_server_type
        ORDER BY server_type ASC
      ) tmp0
    WHERE internal_server_type ILIKE aServerType ;
    IF srvTypeExist is NULL
        THEN srvTypeExist = -1;
    END IF;

    OPEN curs SCROLL FOR
    SELECT
      sas."type"
    FROM (
      SELECT
        tmp0.server_type as "type"
      FROM (
        SELECT DISTINCT server_type
        FROM public_all_servers_sas aa
        JOIN tmp_shuttle_menu sm
          ON sm.name ILIKE aa.internal_server_type
          AND sm.tmp_id = aTmpID
          AND parent_id = id1
        WHERE user_id = aUserid
          AND internal_server_type IS NOT NULL
        ORDER BY server_type ASC
      ) tmp0
    ) sas;

    LOOP
      FETCH curs INTO my_row.type;
      EXIT WHEN NOT FOUND;
      my_row."ServerTypeExist" := srvTypeExist;
      RETURN NEXT my_row;
    END LOOP;
    CLOSE curs;
    RETURN;
  END;
  $$;


