-- Name: get_user_node_groups(); Type: FUNCTION; Schema: sg; Owner: servergraph
--

CREATE FUNCTION get_user_node_groups() RETURNS void
    LANGUAGE plpgsql
    AS $$
    DECLARE ng_id     INT   DEFAULT 0;
    DECLARE access_id INT   DEFAULT 0;
    DECLARE affected_row           INT             DEFAULT 0;
    BEGIN

      SELECT s.id INTO access_id FROM Symbols s , Symbols p WHERE s.name='view' AND s.namespace=p.id AND p.name='permission' AND p.namespace=0;
      SELECT s.id INTO ng_id FROM Symbols s WHERE s.name='node-group' AND s.namespace=0;


      TRUNCATE TABLE user_node_group_list;

      -- find all node groups that is assigned to user group or user
      INSERT INTO user_node_group_list SELECT assign.subject as user_id, assign.object as ng_id FROM Assignments assign WHERE assign.relation=access_id AND (assign.object in ( SELECT id from NodeGroups )) GROUP BY  assign.subject, assign.object; 



      GET DIAGNOSTICS affected_row = ROW_COUNT;

      WHILE affected_row >0 LOOP
         INSERT INTO user_node_group_list 
         SELECT
           t.user_id,
           t.member AS ng_id
         FROM
             ( SELECT  gl.user_id , gr.member from user_node_group_list as gl  JOIN Groups AS gr ON (gl.ng_id = gr.group)
             WHERE gr.member in ( SELECT id from NodeGroups ) ) AS t  
         LEFT JOIN user_node_group_list ungl ON ( t.user_id=ungl.user_id AND t.member = ungl.ng_id ) 
         WHERE ( ungl.user_id IS NULL AND ungl.ng_id IS NULL ) ON CONFLICT DO NOTHING;


         GET DIAGNOSTICS affected_row = ROW_COUNT;

      END LOOP;


      -- find user that in UserGroup that sees node groups
      INSERT INTO user_node_group_list
      SELECT 
        t.user_id,
        t.ng_id
      FROM  
        ( SELECT gr.member AS user_id , gl.ng_id AS ng_id FROM user_node_group_list AS gl JOIN Groups AS gr ON (gl.user_id = gr.group) GROUP BY gr.member, gl.ng_id ) AS t 
      LEFT JOIN user_node_group_list ungl ON ( t.user_id=ungl.user_id AND t.ng_id= ungl.ng_id )
      WHERE ( ungl.user_id IS NULL AND ungl.ng_id IS NULL ) ON CONFLICT DO NOTHING;

    END;
  $$;


