CREATE FUNCTION get_user_permissions(user_name text) RETURNS text
    LANGUAGE plpgsql
    AS $$
    DECLARE
    DECLARE v_name  VARCHAR(200)    DEFAULT '';
    DECLARE done INT        DEFAULT 0;
    DECLARE first INT       DEFAULT 0;
    DECLARE user_permissions TEXT ;


    DECLARE data_1 CURSOR FOR
      SELECT distinct perm.name
      FROM Symbols perm,
           Assignments assign
      WHERE
         assign.relation= ( SELECT f.id FROM Symbols f JOIN Symbols p ON f.namespace = p.id WHERE f.name='features' and p.name='permission' )
         AND (assign.subject in (SELECT "group" FROM Groups WHERE member= (SELECT id FROM Symbols WHERE namespace=1 AND name=user_name )) OR (assign.subject = (SELECT id FROM Symbols WHERE namespace=1 AND name=user_name ) ) )
         AND assign.object=perm.id ORDER by perm.name DESC;

    BEGIN
      user_permissions='';
      OPEN data_1;
      LOOP
        FETCH data_1 INTO v_name;
        EXIT WHEN NOT FOUND;
        IF first = 0 THEN
           user_permissions=v_name;
           first = 1;
        ELSE
           user_permissions=v_name ||  ':' ||  user_permissions;
        END IF;
        END LOOP;
      CLOSE data_1;
      RETURN user_permissions;
    END;
  $$;


