CREATE FUNCTION manage_allnodes_assignment() RETURNS void
    LANGUAGE plpgsql
    AS $$
  DECLARE subject INT DEFAULT 0;
  DECLARE object INT DEFAULT 0;
  DECLARE relation INT DEFAULT 0;
  -- get list of user who sees AllNodes NG
  DECLARE user_cur CURSOR FOR select  s.id from Assignments a, Symbols s, Symbols o, Symbols r, Symbols ng  where s.id=a.subject and o.id=a.object and r.id=a.relation and r.name='view' and o.name='AllNodes' and ng.id=o.namespace and ng.name='node-group';
  BEGIN

      SELECT sg.id INTO object FROM Symbols s , Symbols sg WHERE sg.namespace=s.id and s.name='server-group' AND s.namespace=0 and sg.name='AllNodes';
    
      SELECT s.id INTO relation FROM Symbols s , Symbols p WHERE s.name='view' AND s.namespace=p.id AND p.name='permission' AND p.namespace=0;
    
      OPEN user_cur;
      LOOP
          FETCH user_cur INTO subject;
          EXIT WHEN NOT FOUND;
          -- check if current server exist in AllNodes server group
          EXECUTE 'INSERT INTO Assignments(subject,object, relation) SELECT ' || subject || ',' || object || ',' ||  relation || '  WHERE NOT EXISTS (  select  id from Assignments  where object =' || object || ' AND relation =' || relation || ' AND subject=' || subject  || ')' ;

        END LOOP;
      CLOSE user_cur;

   END;
  $$;


ALTER FUNCTION sg.manage_allnodes_assignment() OWNER TO servergraph;

