--
-- TOC entry 1770 (class 1255 OID 16795)
-- Name: manage_sgint_user(); Type: FUNCTION; Schema: sg; Owner: servergraph
--

CREATE FUNCTION manage_sgint_user() RETURNS void
    LANGUAGE plpgsql
    AS $$
  DECLARE ng_id INT DEFAULT 0;
  DECLARE sgint_name VARCHAR(80) DEFAULT '';
  DECLARE user_name VARCHAR(80) DEFAULT '';
  DECLARE user_namespace INT DEFAULT 0;
  DECLARE view_symbol INT DEFAULT 0;
  DECLARE sg_id INT DEFAULT 0;
  DECLARE user_id INT DEFAULT 0;
  DECLARE ng_cur CURSOR FOR select ng.id from Symbols n, NodeGroups ng where ng.id=n.id;
  DECLARE sgint_cur CURSOR FOR select replace(s.name,'sgint_','') from Symbols s, Users u where u.id=s.id and s.name like 'sgint_%';

  BEGIN

      -- remove sgini_ user for deleted node group
      OPEN sgint_cur;
      LOOP
          FETCH sgint_cur INTO sgint_name;
          EXIT WHEN NOT FOUND;

          -- check if node group exists
          IF NOT EXISTS ( SELECT s.name FROM Symbols s, NodeGroups n WHERE s.id=n.id AND s.name=sgint_name )
          THEN
              -- remove sgint user  from Symbols , Users
              EXECUTE 'DELETE  FROM Assignments WHERE subject in ( SELECT id FROM Symbols WHERE name=''sgint_' || sgint_name || ''')';
              EXECUTE 'DELETE FROM Users WHERE id IN ( SELECT id FROM Symbols WHERE name=''sgint_' || sgint_name || ''')' ;
              EXECUTE 'DELETE FROM Symbols WHERE  name=''sgint_' || sgint_name || '''';
          END IF;
      END LOOP;
      CLOSE sgint_cur;

      -- add sgint if not exists
      OPEN ng_cur;
      LOOP
          FETCH ng_cur INTO  ng_id;
          EXIT WHEN NOT FOUND;

          SELECT 'sgint_' || name into user_name FROM Symbols WHERE id=ng_id ;
          SELECT id INTO user_namespace FROM Symbols WHERE flags='system' AND name='user' AND namespace=0;
          SELECT r.id INTO view_symbol FROM Symbols r, Symbols p  WHERE p.id=r.namespace AND r.name='view' AND p.namespace=0 AND p.name='permission';
          -- try to find whether there is server group id
          SELECT id INTO sg_id FROM Symbols WHERE name = (SELECT name FROM Symbols WHERE id=ng_id) AND namespace=(SELECT id FROM Symbols WHERE name='server-group' AND namespace=0) ;

          -- create sgint_ users if not exists
          EXECUTE 'INSERT INTO Symbols (namespace,name,lastmod ) SELECT ' || user_namespace || ' , ''' || user_name || ''', NOW()  WHERE NOT EXISTS ( SELECT id FROM Symbols WHERE name = ''' || user_name || ''' AND namespace=''' || user_namespace || ''')';

          EXECUTE 'SELECT id FROM Symbols WHERE name=''' || user_name || ''' and namespace=''' || user_namespace || '''' INTO user_id;
          EXECUTE 'INSERT INTO Users (id, full_name) SELECT ' || user_id || ' , ''internal''  WHERE NOT EXISTS ( SELECT id FROM Users WHERE id = ' || user_id || ')' ;

          -- create Assignment for user /ng if not exists
          EXECUTE 'INSERT INTO Assignments(subject,object,relation,lastmod) SELECT ' || user_id || ' ,' || ng_id || ',' || view_symbol || ', NOW() WHERE NOT EXISTS ( SELECT id FROM  Assignments WHERE subject=' || user_id || ' AND object=' || ng_id || ' AND relation= ' || view_symbol || ')';

          IF sg_id IS NOT NULL 
          THEN
              EXECUTE 'INSERT INTO Assignments(subject,object,relation,lastmod) SELECT ' || user_id || ' ,' || sg_id || ',' || view_symbol || ', NOW()  WHERE NOT EXISTS ( SELECT id FROM  Assignments WHERE subject=' || user_id || ' AND object=' || sg_id || ' AND relation= ' || view_symbol || ')';
          END IF;
      END LOOP;
      CLOSE ng_cur;

   END;
  $$;


ALTER FUNCTION sg.manage_sgint_user() OWNER TO servergraph;

