-- TOC entry 1764 (class 1255 OID 16787)
-- Name: node_cleanup(); Type: FUNCTION; Schema: sg; Owner: servergraph
--

CREATE FUNCTION node_cleanup() RETURNS void
    LANGUAGE plpgsql
    AS $$
    BEGIN
    
    --
    -- Create a temporary table to hold the invalid / removed ids.
    --

    CREATE TEMPORARY TABLE IF NOT EXISTS bad_node_ids_tmp (LIKE bad_node_ids);
    INSERT INTO bad_node_ids_tmp (id, namespace, name, PROBLEM_TYPE) 
       select Symbols.id id, Symbols.namespace, Symbols.name, 'MISSING_IN_NODES' 
       from Symbols, Servers
       where  Symbols.namespace = Servers.id
       and Symbols.id not in (select id from Nodes) GROUP BY 1,2,3
    UNION
       select Nodes.id id, Nodes.server_id, Nodes.name, 'MISSING_IN_SYMBOLS' 
       from Nodes  where Nodes.id not in (select id from Symbols) GROUP BY 1,2,3; 

    INSERT INTO bad_node_ids SELECT n.* FROM bad_node_ids_tmp n LEFT JOIN bad_node_ids t on ( n.id=t.id and n.namespace=t.namespace and n.name= t.name) where ( t.id IS NULL AND t.namespace IS NULL AND t.name IS NULL );
   
    --
    -- Well, we have covered all cases. Now delete the spurious data.
    --
    delete from Symbols where Symbols.id in (select id from bad_node_ids where time_solved is NULL);
    delete from Nodes where Nodes.id in (select id from bad_node_ids where time_solved is NULL);
    
    --
    -- Finally, store the correction time, so that we do not re-process this data, and also
    -- can later view it for debug purpose.
    --
    update bad_node_ids set time_solved=now() where time_solved is NULL;

   END;
  $$;


ALTER FUNCTION sg.node_cleanup() OWNER TO servergraph;

