-- TOC entry 1757 (class 1255 OID 16780)
-- Name: populate_ng_members(); Type: FUNCTION; Schema: sg; Owner: servergraph
--

CREATE FUNCTION populate_ng_members() RETURNS void
    LANGUAGE plpgsql
    AS $$
  DECLARE g_id INT;
  DECLARE g_rule           VARCHAR(255);
  DECLARE g_domain         VARCHAR(64);
  DECLARE v_node_group_id  INT DEFAULT 0;
  DECLARE v_ng_id          INT DEFAULT 0;
  DECLARE curs             refcursor;

  BEGIN
    SELECT id INTO v_node_group_id FROM Symbols WHERE namespace=0 AND name='node-group';
    TRUNCATE TABLE ng_members;
    OPEN curs SCROLL FOR SELECT "group", rule , domain FROM GroupRules;
    -- populate nodes from grouprules  
    LOOP
        FETCH curs INTO g_id, g_rule, g_domain;
        EXIT WHEN NOT FOUND;
        IF ( g_rule = '1' )
          THEN g_rule = '1 = 1';
        END IF;
        IF ( g_domain = 'Server' )
          THEN 
            SELECT ng.id INTO v_ng_id FROM NodeGroups, Symbols ng , Symbols sg
            WHERE NodeGroups.id=ng.id
            AND sg.name=ng.name
            AND sg.id=g_id;
            EXECUTE 'INSERT INTO ng_members SELECT ' || v_node_group_id  || ', ' || v_ng_id || ' , id from Nodes where server_id IN (SELECT id FROM Servers WHERE ' || g_rule || ' ) ON CONFLICT DO NOTHING'; 
        ELSIF ( g_domain = 'Node' )
          THEN 
            EXECUTE 'INSERT INTO ng_members SELECT ' || v_node_group_id  || ', ' || g_id || ' , id from Nodes where ' || g_rule || ' ON CONFLICT DO NOTHING'; 
        END IF;
      END LOOP;
    CLOSE curs;

    PERFORM populate_server_group_nodes() ;
    PERFORM show_node_groups_members();
    END;
  $$;


ALTER FUNCTION sg.populate_ng_members() OWNER TO servergraph;

--
-- TOC entry 1760 (class 1255 OID 16783)
-- Name: populate_server_group_nodes(); Type: FUNCTION; Schema: sg; Owner: servergraph
--

CREATE FUNCTION populate_server_group_nodes() RETURNS void
    LANGUAGE plpgsql
    AS $$
    DECLARE v_node_group_id        INT             DEFAULT 0;
    BEGIN

      SELECT id INTO v_node_group_id FROM Symbols WHERE namespace=0 AND name='node-group';

      -- find the list of nodes in SG with same name of NG
      CREATE TEMPORARY TABLE ng_members_tmp ( like ng_members);

      INSERT INTO ng_members_tmp SELECT v_node_group_id, NodeGroups.id AS ng_id, Nodes.id FROM NodeGroups, Symbols ng , Symbols sg , Symbols  s, Groups g  , Nodes  
      WHERE NodeGroups.id=ng.id 
        AND sg.namespace=s.id 
        AND s.name='server-group' 
        AND sg.name=ng.name 
        AND g.group=sg.id 
        AND g.member=Nodes.server_id 
      GROUP BY v_node_group_id, NodeGroups.id, Nodes.id ;

      INSERT INTO ng_members SELECT t.* FROM ng_members_tmp t LEFT JOIN ng_members n on ( n.ng_group_id=t.ng_group_id and n.ng_id=t.ng_id and n.ng_trunk_id = t.ng_trunk_id) WHERE ( n.ng_trunk_id IS NULL AND n.ng_group_id IS NULL AND n.ng_id IS NULL );

    END;
  $$;


