--

CREATE FUNCTION populate_user_viewable_sgs() RETURNS void
    LANGUAGE plpgsql
    AS $$
  DECLARE g_rule           VARCHAR(255);
  DECLARE g_id             INT DEFAULT 0;
  DECLARE nodegroupid      INT DEFAULT 0;
  DECLARE curs             refcursor;
  BEGIN
      CREATE TEMPORARY TABLE IF NOT EXISTS sg_servers_map (
       sg_id INT NOT NULL DEFAULT '0',
       ng_id INT NOT NULL DEFAULT '0',
       server_id INT NOT NULL DEFAULT '0',
       PRIMARY KEY (sg_id, server_id)
      );

      OPEN curs SCROLL FOR SELECT "group", rule FROM GroupRules WHERE domain='Server';
      -- populate sg from grouprules
      LOOP
          FETCH curs INTO g_id, g_rule;
          EXIT WHEN NOT FOUND;
          IF ( g_rule = '1' )
            THEN g_rule = '1 = 1';
          END IF;
          select ng.id into nodegroupid from NodeGroups ng, Symbols ngs, Symbols sgs where ng.id=ngs.id and ngs.name=sgs.name and ngs.namespace=(select id from Symbols where name='node-group' and namespace=0) and sgs.id=g_id;
          EXECUTE 'INSERT INTO sg_servers_map SELECT ' || g_id  || ',' || nodegroupid || ', id from Servers where ' || g_rule || ' ON CONFLICT DO NOTHING';
        END LOOP;
      CLOSE curs;

      DELETE FROM user_viewable_sgs;
      INSERT INTO user_viewable_sgs
      -- user assignment
      SELECT NOW(), a.subject, g.group, g.member FROM Assignments a, Groups g,  Symbols s, Symbols ns, Symbols u,  Symbols un, Servers se  WHERE a.object=g.group  AND s.id=g.group AND ns.id=s.namespace AND ns.name='server-group' AND u.id=a.subject AND u.namespace =un.id AND un.name='user' and se.id=g.member and (se.display is true ) GROUP BY a.subject, g.member, g.group
      UNION
      SELECT NOW(), a.subject, g.group, g.member FROM Assignments a, NodeGroups ng , Symbols ng_symbol, Groups g,  Symbols s, Symbols ns, Symbols u,  Symbols un, Servers se  WHERE a.object=ng.id  AND ng.id=ng_symbol.id AND ng_symbol.name=s.name and s.id=g.group AND ns.id=s.namespace AND ns.name='server-group' AND u.id=a.subject AND u.namespace =un.id AND un.name='user' and se.id=g.member and (se.display is true )  GROUP BY a.subject, g.member, g.group
      UNION
      -- user group assignment
      SELECT NOW(), ug.member, g.group,g.member FROM Assignments a, Groups g,  Symbols s, Symbols ns , Groups ug,  Symbols ug_symbol , Symbols ugns,  Servers se   WHERE a.object=g.group AND s.id=g.group AND ns.id=s.namespace AND ns.name='server-group' AND ug.group=a.subject AND ug.group=ug_symbol.id AND  ugns.id=ug_symbol.namespace AND ugns.name='user-group'  and se.id=g.member and (se.display is true)   GROUP BY ug.member, g.member, g.group
      UNION
      -- sg assignment
      SELECT NOW(), ug.member, sg.sg_id, sg.server_id FROM Assignments a, sg_servers_map sg, Groups ug,  Symbols ug_symbol , Symbols ugns,  Servers se   WHERE a.object=sg.ng_id AND ug.group=a.subject AND ug.group=ug_symbol.id AND  ugns.id=ug_symbol.namespace AND ugns.name='user-group'  and se.id=sg.server_id and (se.display is true)   GROUP BY ug.member, sg.server_id, sg.ng_id, sg.sg_id
      UNION
      -- user sg assignment
      SELECT NOW(), a.subject, sg.sg_id, sg.server_id FROM Assignments a, sg_servers_map sg  WHERE a.object=sg.sg_id GROUP BY a.subject, sg.server_id, sg.ng_id, sg.sg_id;

    END;
  $$;


