-- Name: rename_server_name(character, character); Type: FUNCTION; Schema: sg; Owner: servergraph
--

CREATE FUNCTION rename_server_name(current_servername character, new_servername character) RETURNS void
    LANGUAGE plpgsql
    AS $$
  DECLARE table_name_1 VARCHAR(256)  DEFAULT '';
  DECLARE table_name_2 VARCHAR(256)  DEFAULT '';
  DECLARE insert_qry VARCHAR(256)  DEFAULT '';
  -- get the table that contains servername column
  DECLARE c1 CURSOR FOR SELECT t.TABLE_NAME FROM INFORMATION_SCHEMA.TABLES t JOIN (SELECT table_name from INFORMATION_SCHEMA.COLUMNS WHERE TABLE_SCHEMA='sg' AND COLUMN_NAME = 'servername' GROUP BY table_name ) AS c on ( t.table_name = c.table_name ) WHERE t.TABLE_SCHEMA='sg' AND t.table_type='BASE TABLE' ;
  -- get the table that contains server_name column
  DECLARE c2 CURSOR FOR SELECT t.TABLE_NAME FROM INFORMATION_SCHEMA.TABLES t JOIN (SELECT table_name from INFORMATION_SCHEMA.COLUMNS WHERE TABLE_SCHEMA='sg' AND COLUMN_NAME = 'server_name' GROUP BY table_name ) AS c on ( t.table_name = c.table_name ) WHERE t.TABLE_SCHEMA='sg' AND t.table_type='BASE TABLE';

  BEGIN

      -- update base tables
      UPDATE Symbols SET name = new_servername FROM Servers S WHERE S.id=Symbols.id AND s.name=current_servername;
      UPDATE Servers SET name=new_servername WHERE name = current_servername;
    
      OPEN c1;
      LOOP
          FETCH c1 INTO table_name_1;
          EXIT WHEN NOT FOUND;
          EXECUTE 'UPDATE ' || table_name_1 || ' SET servername=''' ||  new_servername || ''' WHERE servername=''' || current_servername || '''';
      END LOOP;
      CLOSE c1;

      OPEN c2;
      LOOP
          FETCH c2 INTO table_name_2;
          EXIT WHEN NOT FOUND;
          EXECUTE 'UPDATE ' || table_name_2 || ' SET server_name=''' ||  new_servername || ''' WHERE server_name='''|| current_servername || '''';
      END LOOP;
      CLOSE c2;

   END;
  $$;


ALTER FUNCTION sg.rename_server_name(current_servername character, new_servername character) OWNER TO servergraph;

