-- TOC entry 1758 (class 1255 OID 16781)
-- Name: show_node_groups_members(); Type: FUNCTION; Schema: sg; Owner: servergraph
--

CREATE FUNCTION show_node_groups_members() RETURNS void
    LANGUAGE plpgsql
    AS $$
  DECLARE v_node_group_id        INT             DEFAULT 0;
  DECLARE affected_row           INT             DEFAULT 0;
    BEGIN
      SELECT id INTO v_node_group_id FROM Symbols WHERE namespace=0 AND name='node-group';

      CREATE TEMPORARY TABLE IF NOT EXISTS tmpLst (
       id INT NOT NULL DEFAULT '0',
       group_id INT NOT NULL DEFAULT '0',
       member INT NOT NULL DEFAULT '0',
       PRIMARY KEY (id,group_id,member)
      );
     
      INSERT into tmpLst
      SELECT
          gr.id,
          gr.group as group_id,
          gr.member as member
      FROM
          Groups as gr
          JOIN NodeGroups as ng ON (gr.group = ng.id);

      INSERT INTO tmpLst SELECT n.* FROM ng_members n LEFT JOIN tmpLst t on ( n.ng_group_id=t.id and n.ng_id=t.group_id and n.ng_trunk_id = t.member) where ( t.id IS NULL AND t.group_id IS NULL AND t.member IS NULL );

      GET DIAGNOSTICS affected_row = ROW_COUNT;         

      WHILE affected_row >0 LOOP 
           insert into tmpLst
           select
               tmp2.id,tmp1.group_id, tmp2.member
           from
               tmpLst as tmp1
               JOIN tmpLst as tmp2 ON (tmp1.member = tmp2.group_id) ON CONFLICT DO NOTHING;
           GET DIAGNOSTICS affected_row = ROW_COUNT;         
      END LOOP;
     
      DELETE  FROM tmpLst WHERE member IN ( SELECT group_id FROM tmpLst ) ;

      INSERT INTO ng_members SELECT t.* FROM tmpLst t LEFT JOIN ng_members n on ( n.ng_group_id=t.id and n.ng_id=t.group_id and n.ng_trunk_id = t.member) WHERE ( n.ng_group_id IS NULL AND n.ng_id IS NULL AND n.ng_trunk_id IS NULL); 
    
    END;
  $$;


ALTER FUNCTION sg.show_node_groups_members() OWNER TO servergraph;

