--
-- TOC entry 1787 (class 1255 OID 16812)
-- Name: update_metadata_tree_with_internal_server_type(); Type: FUNCTION; Schema: sg; Owner: servergraph
--

CREATE FUNCTION update_metadata_tree_with_internal_server_type() RETURNS void
    LANGUAGE plpgsql
    AS $$
  BEGIN
    -- remove the leading 0;
    UPDATE brb_templates
    SET metadata_tree = SPLIT_PART(metadata_tree,'0;',2)
    WHERE metadata_tree LIKE '0;%';

    -- update metadata_tree to use internal server type names
    UPDATE brb_templates orig
    SET metadata_tree = REPLACE (orig.metadata_tree , old_type , server_type)
    FROM
    ( SELECT metadata_tree, server_type , old_type FROM
        ( SELECT SPLIT_PART(metadata_tree,'/',2) AS old_type ,
               metadata_tree FROM brb_templates
         ) AS old JOIN server_types_mapping ON (display_name=old_type)
      UNION
      SELECT metadata_tree , 'tsm' , 'TSM' FROM brb_templates where metadata_tree like '%/TSM/%'
      UNION
      SELECT metadata_tree , 'ibmi' , 'IBMi' FROM brb_templates where metadata_tree like '%/IBMi/%'
      UNION
      SELECT metadata_tree , 'hyperv' , 'HyperV' FROM brb_templates where metadata_tree like '%/HyperV/%'
      UNION
      SELECT metadata_tree , 'netapp' , 'NetApp' FROM brb_templates where metadata_tree like '%/NetApp/%'
      UNION
      SELECT metadata_tree , 'commvault' , 'CommVault' FROM brb_templates where metadata_tree like '%/CommVault/%'
      UNION
      SELECT metadata_tree , 'mssql' , 'MSSQL' FROM brb_templates where metadata_tree like '%/MSSQL/%'
      UNION
      SELECT metadata_tree , 'quantum' , 'Quantum' FROM brb_templates where metadata_tree like '%/Quantum/%'
      UNION
      SELECT metadata_tree , 'sepaton' , 'Sepaton' FROM brb_templates where metadata_tree like '%/Sepaton/%'
      UNION
      SELECT metadata_tree , 'networker' , 'Networker' FROM brb_templates where metadata_tree like '%/Networker/%'
    ) AS new
    WHERE orig.metadata_tree = new.metadata_tree;
  END;
  $$;


ALTER FUNCTION sg.update_metadata_tree_with_internal_server_type() OWNER TO servergraph;

