    repl_sprule = EXCLUDED.repl_sprule,\n          bkp_stat    = EXCLUDED.bkp_stat,\n          bkp_stat_name       = EXCLUDED.bkp_stat_name,\n          bkp_stat_timestamp  = EXCLUDED.bkp_stat_timestamp,\n          capacity    = EXCLUDED.capacity,\n          cap_used    = EXCLUDED.cap_used,\n          pct_full    = EXCLUDED.pct_full,\n          hog_factor  = EXCLUDED.hog_factor,\n          pct_chg     = EXCLUDED.pct_chg,\n          stg_pri_gr  = EXCLUDED.stg_pri_gr,\n          stg         = EXCLUDED.stg,\n          stg_pri     = EXCLUDED.stg_pri,\n          stg_cpy     = EXCLUDED.stg_cpy,\n          stg_diff    = EXCLUDED.stg_diff,\n          stg_bak     = EXCLUDED.stg_bak,\n          stg_bak_pri = EXCLUDED.stg_bak_pri,\n          stg_bak_cpy = EXCLUDED.stg_bak_cpy,\n          stg_arc     = EXCLUDED.stg_arc,\n          stg_arc_pri = EXCLUDED.stg_arc_pri,\n          stg_arc_cpy = EXCLUDED.stg_arc_cpy,\n          stg_hsm     = EXCLUDED.stg_hsm,\n          stg_hsm_pri = EXCLUDED.stg_hsm_pri,\n          stg_hsm_cpy = EXCLUDED.stg_hsm_cpy,\n          num_files   = EXCLUDED.num_files,\n          idlewait    = EXCLUDED.idlewait,\n          commwait    = EXCLUDED.commwait,\n          mediawait   = EXCLUDED.mediawait,\n          ses_time    = EXCLUDED.ses_time,\n          ses_size    = EXCLUDED.ses_size,\n          ses_rate    = EXCLUDED.ses_rate,\n          insp        = EXCLUDED.insp,\n          bkp         = EXCLUDED.bkp,\n          fail        = EXCLUDED.fail,\n          res         = EXCLUDED.res,\n          arc         = EXCLUDED.arc,\n          ret         = EXCLUDED.ret,\n          ignored     = EXCLUDED.ignored,\n          email_address       = EXCLUDED.email_address,\n          decommissioned      = EXCLUDED.decommissioned,\n          nodegroup           = EXCLUDED.nodegroup,\n          decommissioned_time = EXCLUDED.decommissioned_time	\N	\N	\N	\N	\N	\N	\N	server_name	\N		\N	f	f
