tsm_vms	SG::Datasource::DBI	INSERT INTO tsm_vms SELECT\n       stg.server_id,\n       stg.node_id,\n       n.id as vm_id,\n       stg.filespace_id,\n       COALESCE(ses.bkp_stat,CASE WHEN stg.days_since_last_completed=0 THEN 2 ELSE NULL END,-4),\n       COALESCE(ses.bkp_stat_name,CASE WHEN stg.days_since_last_completed=0 THEN 'COMPLETED' ELSE NULL END,'UNKNOWN'),\n       COALESCE(stg.days_since_last_completed,t.days_since_last_completed,-1),\n       COALESCE(stg.last_completed,t.last_completed),\n       CASE WHEN stg.last_completed IS NOT NULL THEN 'filespaces'::bkp_stat_source_enum WHEN t.last_completed IS NOT NULL THEN 'sessions'::bkp_stat_source_enum ELSE NULL END,\n       NOW(),\n       stg.capacity,\n       stg.pct_util,\n       stg.stg,\n       stg.stg_pri,\n       stg.stg_cpy,\n       stg.stg_bak,\n       stg.stg_bak_pri,\n       stg.stg_bak_cpy,\n       stg.stg_arc,\n       stg.stg_arc_pri,\n       stg.stg_arc_cpy,\n       stg.stg_hsm,\n       stg.stg_hsm_pri,\n       stg.stg_hsm_cpy,\n       stg.num_files,\n       ses.ses_time,\n       ses.ses_size,\n       ses.ses_rate,\n       ses.insp,\n       ses.bkp,\n       ses.fail,\n       ses.arc,\n       ses.upd,\n       ses.reb,\n       ses.del,\n       ses.exp,\n       ses.bytes_insp,\n       ses.bytes_tran ,\n       ses.job_result_code\n       FROM (\n       SELECT\n              ptf.server_id                                                                                         AS server_id,\n              ptf.node_id                                                                                           AS node_id,\n              ptf.filespace_id                                                                                      AS filespace_id,\n              CASE WHEN ptf.filespace_type  = 'api:tsmvm' THEN REPLACE(ptf.filespace_name, '\\VMFULL-', '') \n                   WHEN ptf.filespace_type  = 'vss' THEN SPLIT_PART(SPLIT_PART(ptf.filespace_name, '}\\', '2'),'\\',1)  \n                   WHEN  ptf.filespace_type  = 'API:VCDVM' THEN SPLIT_PART(SPLIT_PART(ptf.filespace_name, '::', '4'),' (',1)\n              END AS vm_name,\n              ptf.capacity          as capacity,\n              ptf.pct_util as pct_util,\n              SUM(case when pto.type = 'Arch' then logical_mb * 1048576 else 0 end)                                                       AS stg_arc,\n              SUM(case when pto.type = 'Arch' and pooltype = 'PRIMARY' then logical_mb * 1048576 else 0 end)                               AS stg_arc_pri,\n              SUM(case when pto.type = 'Arch' and pooltype = 'COPY' then logical_mb * 1048576 else 0 end)                                  AS stg_arc_cpy,\n              SUM(case when pto.type = 'Bkup' then logical_mb * 1048576 else 0 end)                                                       AS stg_bak,\n              SUM(case when pto.type = 'Bkup' and pooltype = 'PRIMARY' then logical_mb * 1048576 else 0 end)                               AS stg_bak_pri,\n              SUM(case when pto.type = 'Bkup' and pooltype = 'COPY' then logical_mb * 1048576 else 0 end)                                  AS stg_bak_cpy,\n              SUM(case when pto.type = 'Spmg' then logical_mb * 1048576 else 0 end)                                                       AS stg_hsm,\n              SUM(case when pto.type = 'Spmg' and pooltype = 'PRIMARY' then logical_mb * 1048576 else 0 end)                              AS stg_hsm_pri,\n              SUM(case when pto.type = 'Spmg' and pooltype = 'COPY' then logical_mb * 1048576 else 0 end)                                 AS stg_hsm_cpy,\n              SUM(case when pooltype = 'COPY' then logical_mb * 1048576 else 0 end)                                                   AS stg_cpy,\n              SUM(case when pooltype = 'PRIMARY' then logical_mb * 1048576 else 0 end)                                                AS stg_pri,\n              SUM(logical_mb * 1048576)                                                                             AS stg,\n              SUM(num_files)                                                                                        AS num_files,\n              MAX(backup_end)                                                                                       AS last_completed,\n              date_part('day',CURRENT_DATE-MIN(tz_offset)*INTERVAL '1 hour'+time '0600'-MAX(backup_end))      AS days_since_last_completed\n       FROM private_tsm_filespaces as ptf\n       INNER JOIN Servers ON (id=server_id) \n       LEFT OUTER JOIN  private_tsm_occupancy AS pto USING (server_id,node_id,filespace_name)\n       LEFT OUTER JOIN  private_tsm_stgpools  AS pts USING (server_id,stgpool_name)\n       WHERE ptf.server_name=?\n       AND ( ptf.filespace_type  = 'api:tsmvm'  or ( ptf.filespace_name like '%Microsoft Hyper-V VSS Writer%' and ptf.filespace_type  = 'vss') or ptf.filespace_type  = 'API:VCDVM'  )\n       GROUP BY ptf.server_id,\n                ptf.node_id,\n                ptf.filespace_name,\n                ptf.filespace_id) as stg\n       JOIN ( select server_id, N.id, N.name as vm_name, S.namespace node_id from Nodes N , Symbols S where S.id=N.id and domain='VM' and (os='' or os='VSS') and version='' ) n USING (server_id,node_id,vm_name)\n       LEFT OUTER JOIN (\n       SELECT server_id, node_id, vm_name, vm_id, SUM(elapse_time) AS ses_time, SUM(bytes_transferred) AS ses_size, (case when SUM(elapse_time)!=0 then SUM(bytes_transferred)/SUM(elapse_time) else 0 end ) AS ses_rate ,\n              SUM(inspected) AS insp, SUM(backedup) AS bkp, SUM(failed) AS fail, SUM(archived) AS arc, SUM(updated) AS upd, SUM(rebound) as reb, SUM(deleted) as del, SUM(expired) as exp,\n              SUM(bytes_inspected) AS bytes_insp, SUM(bytes_transferred) AS bytes_tran, coalesce(string_agg(return_code::text, ','), ' ') AS job_result_code, AVG(reduction_ratio) AS reduct_ratio,\n              ( case when MAX(success_flag)=1 then 2 else -5 end) AS bkp_stat,  (case when MAX(success_flag)=1 then 'COMPLETED' else 'FAILED' end) AS bkp_stat_name\n       FROM tsm_vm_sessions_info,\n                   ( select id as serid, tz_offset as tzoff from Servers where type='tsm' ) info\n       WHERE server_id=info.serid AND session_start > ((current_date - interval '1 day') + time '0600')\n       GROUP BY server_id,node_id,vm_name, vm_id) ses  USING (server_id,node_id,vm_name)\n       LEFT OUTER JOIN (\n       SELECT server_id, node_id , vm_name, vm_id, date_part('day', CURDATE()-MAX(session_end)) AS days_since_last_completed, MAX(session_end) as last_completed\n       FROM  tsm_vm_sessions_info\n       WHERE success_flag=1\n       GROUP BY server_id, node_id, vm_name, vm_id) t USING (server_id,node_id,vm_name,vm_id) 	\N	\N	\N	\N	\N	\N	\N	server_name	\N		\N	f	f
