update_vm_id_in_tsm_vm_sessions_info	SG::Datasource::DBI	UPDATE tsm_vm_sessions_info as vmsess SET vm_id = vms.vm_id FROM ( SELECT n.id as vm_id, n.server_id, n.name as vm_name, s.namespace as node_id  FROM  Nodes n JOIN Symbols s ON (n.id=s.id )  WHERE n.domain='VM' and (n.os='' or n.os='VSS') and n.version=''  ) as vms\n       where vmsess.server_id=vms.server_id and vmsess.node_id=vms.node_id and vmsess.vm_name=vms.vm_name and vmsess.vm_id = 0	\N	\N	\N	\N	\N	\N	\N		\N		\N	f	f
private_tsm_at_risk_data	SG::Datasource::DBI	INSERT INTO private_tsm_at_risk_data\n      SELECT source_node.server_name as server_name\n          , source_node.server_id as server_id\n          , source_node.node_name as node_name\n          , source_node.node_id as node_id\n          , source_node.stg_pri as primary_bytes\n          , (case when source_node.replication_enabled=1 then\n              (case when source_node.stg_cpy>1 then 'Copy Pool + Node Replication' else 'Node Replication' end) else 'Copy Pool' end) as copy_technic\n          , source_node.stg_cpy + COALESCE(target_node.stg_pri, 0) + COALESCE(target_node.stg_cpy, 0) as copy_bytes\n          , (case when source_node.replication_enabled=1 then\n              (case when (source_node.stg_pri-COALESCE(target_node.stg_pri,0))<0 then\n                  0 else\n                  source_node.stg_pri-COALESCE(target_node.stg_pri,0) end) else\n              (case when stg_diff<0 then 0 else stg_diff end) end) AS at_risk_bytes\n      FROM (\n         SELECT A.server_id, A.node_id, A.server_name, A.node_name,\n                A.stg_pri, B.target_repl_server_name, A.stg_cpy, A.stg_diff,\n                (case when B.outbound_repl='enabled' AND A.repl_mode='send' AND A.repl_state='enabled' then 1 else 0 end) replication_enabled\n         FROM ( SELECT n.server_id, n.node_id, n.server_name, n.node_name , stg_pri, stg_cpy, stg_diff , repl_mode, repl_state\n                FROM private_tsm_nodes n JOIN tsm_node_summary s  on s.server_id = n.server_id and s.node_id = n.node_id ) A\n         JOIN  private_tsm_server_status B\n               ON A.server_id = B.server_id\n         WHERE ( A.repl_mode<>'receive' or A.repl_mode is NULL)\n      ) source_node\n      LEFT JOIN (\n         SELECT C.server_id, C.node_id, C.server_name, C.node_name, C.stg_pri, C.stg_cpy FROM \n         (SELECT n.server_id, n.node_id, n.server_name, n.node_name, s.stg_pri, s.stg_cpy , n.repl_state, n.repl_mode\n                FROM private_tsm_nodes n JOIN tsm_node_summary s  on s.server_id = n.server_id and s.node_id = n.node_id) as C\n          WHERE C.repl_state = 'enabled'\n           AND C.repl_mode = 'receive'\n      ) target_node\n      ON source_node.target_repl_server_name = target_node.server_name AND source_node.node_name = target_node.node_name WHERE source_node.server_name=?\n;	\N	\N	\N	\N	\N	\N	\N	server_name	\N		\N	t	f
