populate_tsm_node_session_daily	SG::Datasource::DBI	INSERT INTO tsm_node_session\n       SELECT SESSION.server_name,\n              SESSION.server_id,\n              SESSION.node_name,\n              SESSION.node_id,\n              SESSION.session_start,\n              SESSION.session_end,\n              SESSION.session_length,\n              SESSION.transfer_rate,\n              SUC_FG.success_flag,\n              SESSION.idle_wait,\n              SESSION.media_wait,\n              SESSION.comm_wait,\n              SESSION.session_id\n       FROM \n      ( -- get session data\n        select\n        server_name,\n        server_id,\n        node_name,\n        node_id,\n        session_id,\n        min(session_start) as session_start,\n        max(session_end) as session_end,\n        extract(epoch FROM max(session_end))-extract(epoch FROM min(session_start)) as session_length,\n        (case when SUM(bytes_total) = 0 then 0 else (case when session_length = 0 then SUM(bytes_total) else SUM(bytes_total)/session_length end) end )as transfer_rate,\n        max(idle_wait) as idle_wait,\n        max(media_wait) as media_wait,\n        max(comm_wait) as comm_wait\n        from private_tsm_node_session\n        where session_end between (current_date - interval '18 hour') and (select current_date + interval '6 hour')\n        and (bytes_archived!=0 or bytes_retrieved!=0 or bytes_backed_up !=0 or bytes_restored !=0 or bytes_total > 200*1024 )\n        group by server_name,server_id,node_name,node_id,session_id,session_length\n      ) as SESSION\n      LEFT JOIN\n      ( -- get session success_flag\n        select T1.server_id,T1.node_id,T1.session_id,T2.success_flag from \n        (select server_id,\n                node_id,\n                session_id,\n                max(session_start) as mx_start\n         from private_tsm_node_session\n         where session_end between (current_date - interval '18 hour') and (select current_date + interval '6 hour')\n         and (bytes_archived!=0 or bytes_retrieved!=0 or bytes_backed_up !=0 or bytes_restored !=0 or bytes_total > 200*1024 )\n         group by session_id,server_id,node_id) as T1\n        LEFT JOIN\n        (select server_id,\n                node_id,\n                session_id,\n                success_flag,       \n                session_start\n         from private_tsm_node_session\n         where session_end between (current_date - interval '18 hour') and (select current_date + interval '6 hour')\n         and (bytes_archived!=0 or bytes_retrieved!=0 or bytes_backed_up !=0 or bytes_restored !=0 or bytes_total > 200*1024 )\n         ) as T2\n        ON (T1.server_id=T2.server_id and T1.node_id=T2.node_id and T1.session_id=T2.session_id and T1.mx_start=T2.session_start)\n      ) as SUC_FG\n      ON (SESSION.server_id=SUC_FG.server_id and SESSION.node_id=SUC_FG.node_id and SESSION.session_id=SUC_FG.session_id)\n;	\N	\N	\N	\N	\N	\N	\N		\N		\N	f	f
tsm_libtapes_info	SG::Datasource::DBI	INSERT INTO tsm_libtapes_info \n       SELECT SE.id AS server_id, \n             SE.name AS server_name,  \n             RTL.library_name AS library_name, \n             (CASE WHEN TLC.slots_count > 0 THEN TLC.slots_count ELSE 0 END) AS number_slots,\n             COUNT(RTLV.status) as occupied_slots, \n             SUM(CASE WHEN RTLV.status = 'scratch' THEN 1 ELSE 0 END) AS scratch_tapes,\n             SUM(CASE WHEN RTLV.status = 'private' THEN 1 ELSE 0 END) AS tapes,\n             (CASE WHEN TLC.slots_count > 0 then (SUM(CASE WHEN RTLV.status = 'private' THEN 1 ELSE 0 END)/TLC.slots_count) ELSE 0 END) AS pctfull \n       FROM Servers SE\n       JOIN Symbols SY ON SY.id=SE.id and SE.type = 'tsm' AND SE.os_type not ilike '%mvs%'\n       JOIN raw_tsm_libraries RTL ON RTL.server_name = SE.name \n       JOIN raw_tsm_libvolumes RTLV ON RTLV.server_name =SE.name AND RTL.library_name=RTLV.library_name\n       LEFT JOIN tsm_libslots_conf TLC ON TLC.server_name = SE.name and lower(TLC.library_name)=RTL.library_name\n       WHERE SE.name = ?\n       GROUP BY 1,2,3,TLC.slots_count	\N	\N	\N	\N	\N	\N	\N	server_name	\N		\N	t	f
tsm_node_session_summary	SG::Datasource::DBI	INSERT INTO tsm_node_session_summary\n       SELECT ptns.server_name,\n         ptns.server_id,\n         ptns.node_name,\n         ptns.node_id,\n         SUM(ptns.session_length * ptns.transfer_rate)                       AS ses_size,\n         CASE WHEN SUM(session_length) > 0 THEN SUM(ptns.session_length * ptns.transfer_rate) / SUM(session_length) ELSE 0 END  AS ses_rate,\n         SUM(ptns.session_length)                                            AS ses_time,\n         SUM(ptns.media_wait)                                                AS mediawait,\n         (case when SUM(ptns.session_length) > 0 then SUM(ptns.media_wait)/SUM(ptns.session_length)*100 else 0 end)     AS mediawait_pct,\n         SUM(ptns.media_wait)                                                AS mediawait_duration,\n         SUM(ptns.idle_wait)                                                 AS idlewait,\n         (case when SUM(ptns.session_length) > 0 then SUM(ptns.idle_wait)/SUM(ptns.session_length)*100 else 0 end)     AS idlewait_pct,\n         SUM(ptns.idle_wait)                                                 AS idlewait_duration,\n         SUM(ptns.comm_wait)                                                 AS commwait, \n         (case when SUM(ptns.session_length) > 0 then SUM(ptns.comm_wait)/SUM(ptns.session_length)*100 else 0 end)     AS commwait_pct,\n         SUM(ptns.comm_wait)                                                 AS commwait_duration\n       FROM tsm_node_session ptns\n       WHERE 1 = 1\n       AND ptns.session_end >= ((current_date - interval '1 day') + time '0600')\n       GROUP BY server_name,server_id,node_name,node_id	\N	\N	\N	\N	\N	\N	\N		\N		\N	f	t
tsm_library_volumes	SG::Datasource::DBI	INSERT INTO tsm_library_volumes\n      SELECT\n      coalesce(libvolume.library_server,'') as library_server\n      , a.server_name\n      , a.server_id\n      , a.volume_name\n      , coalesce(v.stgpool_name,'') as stgpool\n      , coalesce(v.est_capacity_mb::decimal,0) as estcap\n      , coalesce(v.pct_utilized::decimal,0) as pctutil\n      , v.status\n      , v.access\n      , v.scratch\n      , v.devclass_name\n      , v.times_mounted\n      , v.write_pass\n      , v.last_write_date\n      , v.last_read_date\n      , v.pending_date\n      , v.write_errors\n      , v.read_errors\n      , v.location\n      , libvolume.library_name\n      , libvolume.home_element\n      , libvolume.status\n      , dr.state\n      , dr.voltype\n      , libvolume.owner\n      , libvolume.mediatype\n      , v.begin_rclm_date\n      , v.end_rclm_date\n      , v.vol_encr_keymgr\n      , v.drive_encryption\n      FROM \n        ( SELECT server_id , volume_id , server_name , volume_name from private_tsm_volumes UNION SELECT server_id, volume_id, server_name, volume_name FROM private_tsm_drmedia ) as a\n      LEFT JOIN private_tsm_drmedia AS dr  USING (server_id,volume_id)\n      LEFT JOIN ( \n        SELECT vol.* , drive_encryption FROM private_tsm_volumes vol LEFT JOIN private_tsm_devclasses USING (devclass_name,server_id) )  v USING (server_id,volume_id) \n      LEFT JOIN (\n        SELECT lib.server_id, CASE WHEN primary_lib_mgr='' THEN server_name ELSE primary_lib_mgr END  AS library_server, libvol.*\n        FROM\n          ( SELECT CASE WHEN owner ='' THEN server_name ELSE owner END as server,  volume_id, library_name, home_element, status, owner, mediatype from private_tsm_libvolumes   ) AS libvol\n          JOIN private_tsm_libraries  lib ON ( libvol.server = lib.server_name and libvol.library_name = lib.library_name) \n      ) AS libvolume ON (a.server_id=libvolume.server_id AND a.volume_id = libvolume.volume_id)\n      WHERE a.server_name = ? 	\N	\N	\N	\N	\N	\N	\N	server_name	\N		\N	t	f
tsm_volumes_usage_history	SG::Datasource::DBI	insert into tsm_volumes_usage_history\n      select now(), server_name, server_id, library_server, coalesce(library_name,'') as library_name, stgpool, coalesce(mediatype,'') as mediatype,\n      -- Various counts, for All, or split by type scratch, private, other\n      count(volume) as vol_count,\n      sum(CASE WHEN scratch='scratch' then 1 else 0 end) as vol_scratch_count,\n      sum(CASE WHEN scratch='private' then 1 else 0 end) as vol_private_count,\n      sum(CASE WHEN scratch='' then 1 else 0 end) as vol_other_count,\n      sum(CASE WHEN volstat='full' then 1 else 0 end) as vol_full_count,\n      -- Various sum, for the mounts, capacity or errors\n      COALESCE(sum(mounts),0) as vol_mounts,\n      COALESCE(sum(estcap),0) as vol_estcap,\n      COALESCE(sum(write_errs),0) as vol_write_errs,\n      COALESCE(sum(read_errs),0) as vol_read_errs\n      from tsm_library_volumes\n      where ( select COALESCE((max(timestamp) + INTERVAL '6 hour'),now()) <= now() from tsm_volumes_usage_history  ) and server_name = ?\n      group by server_id, server_name, library_server, library_name, stgpool, mediatype 	\N	\N	\N	\N	\N	\N	\N	server_name	\N		\N	f	f
