tsm_sessions_materialized	SG::Datasource::DBI	INSERT INTO tsm_sessions_materialized\n       SELECT ptns.server_id        AS server_id,\n         ptns.server_name      AS server_name,\n         ptns.node_id          AS node_id,\n         ptns.node_name        AS node_name,\n         ptns.success_flag     AS success,\n         ptns.session_start    AS session_start,\n         ptns.session_end      AS session_end,\n         ptns.bytes_total      AS bytes_total,\n         ptns.bytes_backed_up  AS bytes_backed_up,\n         ptns.bytes_restored   AS bytes_restored,\n         ptns.bytes_archived   AS bytes_archived,\n         ptns.bytes_retrieved  AS bytes_retrieved,\n         ptns.bytes_hsm_in     AS bytes_hsm_in,\n         ptns.bytes_hsm_out    AS bytes_hsm_out,\n         ptns.bytes_overhead   AS bytes_overhead,\n         ptns.files_total      AS files_total,\n         ptns.files_backed_up  AS files_backed_up,\n         ptns.files_restored   AS files_restored,\n         ptns.files_archived   AS files_archived,\n         ptns.files_retrieved  AS files_retrieved,\n         ptns.files_hsm_in     AS files_hsm_in,\n         ptns.files_hsm_out    AS files_hsm_out,\n         ptns.session_length   AS session_length,\n         ptns.idle_wait        AS idlewait,\n         ptns.comm_wait        AS commwait,\n         ptns.media_wait       AS mediawait,\n         ptns.scheduled_flag   AS scheduled_flag,\n         tsi.type              AS server_swtype,\n         tsi.sw_version        AS server_swversion,\n         tsi.os_type           AS server_ostype,\n         ptn.ostype            AS node_ostype,\n         ptn.osversion         AS node_osversion,\n         ptn.swversion         AS node_swversion,\n         ptn.domain_name       AS node_domain_name,\n         ptn.locked            AS node_locked,\n         ptn.contact           AS node_contact,\n         ptn.tcp_address       AS node_tcp_address\n       FROM        private_tsm_node_session  AS ptns\n       JOIN Servers           AS tsi ON (     ptns.server_id = tsi.id\n                                                    )\n       JOIN private_tsm_nodes         AS ptn ON (     ptns.server_id = ptn.server_id\n                                                      AND ptns.node_id   = ptn.node_id\n                                                    )	\N	\N	\N	\N	\N	\N	\N		\N		\N	f	t
