tsm_libraries_info	SG::Datasource::DBI	INSERT INTO tsm_libraries_info (server_name, server_id, library_name, library_type, acs_id, external_mgr,shared,primary_lib_mgr,library_serial,autolabel,relabelscratch,number_slots,occupied_slots,scratch_tapes,tapes,mounted,checkin,checkout,number_of_drives )\n      SELECT ptl.server_name       AS server_name,\n         ptl.server_id       AS server_id,\n         ptl.library_name    AS library_name, \n         ptl.library_type    AS library_type,\n         ptl.acs_id          AS acs_id,\n         ptl.external_mgr    AS external_mgr,\n         ptl.shared          AS shared,\n         ptl.primary_lib_mgr AS primary_lib_mgr,\n         ptl.library_serial  AS library_serial,\n         ptl.autolabel       AS autolabel,\n         ptl.relabelscratch  AS relabelscratch,\n         tli.number_slots    AS number_slots,\n         tli.occupied_slots  AS occupied_slots,\n         tli.scratch_tapes   AS scratch_tapes,\n         tli.tapes           AS tapes, \n         tmpdpl.mounted        AS mounted,\n         tlvl.checkin          AS checkin,                 \n         tlvl.checkout         AS checkout,                 \n         td.number_of_drives AS number_of_drives \n      FROM  private_tsm_libraries            AS ptl\n      LEFT OUTER JOIN tsm_libtapes_info  AS tli ON ( ptl.server_name = tli.server_name and ptl.library_name = tli.library_name )\n      LEFT OUTER JOIN (select server_name, library_name, sum((case when volume_activity ='CHECKIN' then 1 else 0 end) )as checkin, sum((case when volume_activity ='CHECKOUT' then 1 else 0 end) )as checkout  from tsm_library_volumes_lifecycle where timestamp > (curdate() - integer '1' ) group by server_name, library_name ) as tlvl \n             ON ( ( ptl.server_name = tlvl.server_name and ptl.library_name = tlvl.library_name) or (  ptl.primary_lib_mgr = tlvl.server_name and ptl.library_name = tlvl.library_name) )\n      LEFT OUTER JOIN ( select server_name, library_name, count(drive_name) as number_of_drives from private_tsm_drives group by server_name, library_name) as td  \n             ON ( ptl.server_name = td.server_name and ptl.library_name = td.library_name)\n      LEFT OUTER JOIN\n              ( SELECT SERVER_ID, d.library_name, SUM(mount) AS mounted FROM \n                    ( SELECT server_id, COUNT(mount_open) AS mount,mount_drive AS drive_name  FROM tsm_mnt_info \n                      WHERE mount_open > (NOW() - INTERVAL '1 DAY') AND mount_type ='tape' GROUP BY 1, 3 ) AS drive_mnt \n                JOIN private_tsm_drives d USING (server_id, drive_name) GROUP BY 1, 2\n              ) AS  tmpdpl \n             ON ( ptl.server_id = tmpdpl.server_id AND ptl.library_name = tmpdpl.library_name) where ptl.server_name=?	\N	\N	\N	\N	\N	\N	\N	server_name	\N		\N	f	f
