private_tsm_node_mgmt	SG::Datasource::DBI	INSERT INTO private_tsm_node_mgmt\n      SELECT n.server_id, n.node_id, n.server_name, n.node_name, n.domain_name, option_set,COALESCE(mgmt, defmgmtclass)  AS class_name\n      FROM  private_tsm_nodes n LEFT JOIN\n        -- only list the class defined in private_tsm_mgmtclasses\n        (SELECT o.server_id, optionset_name, mgmt FROM\n          ( SELECT o.server_id, optionset_name, SPLIT_PART(option_value, ' ', '3')  as mgmt FROM private_tsm_clientopts o WHERE  option_name='inclexcl'\n          AND option_value LIKE 'include%' and SPLIT_PART(option_value, ' ', '3') !='' GROUP BY 1,2,3\n          ) o\n          JOIN (SELECT server_id , class_name FROM  private_tsm_mgmtclasses WHERE set_name='ACTIVE' GROUP BY 1,2) m\n          ON  m.server_id=o.server_id AND m.class_name = mgmt\n        ) c ON  n.option_set=c.optionset_name AND n.server_id=c.server_id\n      JOIN private_tsm_policyset dm ON  n.server_id=dm.server_id AND n.domain_name = dm.domain_name\n      WHERE n.option_set <>'' AND set_name='ACTIVE' AND n.server_name=?\n      UNION\n      select  n.server_id, n.node_id, n.server_name, n.node_name, n.domain_name, dm.set_name,dm.defmgmtclass\n      FROM private_tsm_nodes n\n      JOIN private_tsm_policyset dm ON n.server_id=dm.server_id AND n.domain_name = dm.domain_name\n      WHERE n.option_set ='' AND set_name='ACTIVE' AND n.server_name=?	\N	\N	\N	\N	\N	\N	\N	server_name server_name	\N		\N	f	t
