it_pct                  AS idlewait_pct,\n         tns.idlewait_duration             AS idlewait_duration,\n         tns.commwait                      AS commwait,\n         tns.commwait_pct                  AS commwait_pct,\n         tns.commwait_duration             AS commwait_duration,\n         tns.mediawait                     AS mediawait,\n         tns.mediawait_pct                 AS mediawait_pct,\n         tns.mediawait_duration            AS mediawait_duration,\n         tns.ses_time                      AS ses_time,\n         tns.ses_size                      AS ses_size,\n         tns.ses_rate                      AS ses_rate,\n         tns.insp                          AS insp,\n         tns.bkp                           AS bkp,\n         tns.fail                          AS fail,\n         tns.res                           AS res,\n         tns.arc                           AS arc,\n         tns.ret                           AS ret,\n         tns.upd                           AS upd,\n         tns.reb                           AS reb,\n         tns.del                           AS del,\n         tns.exp                           AS exp,\n         tns.dup                           AS dup,\n         tns.bytes_insp                    AS bytes_insp,\n         tns.bytes_tran                    AS bytes_tran,\n         tns.reduct_ratio                  AS reduct_ratio,\n         tns.ignored                       AS ignored,\n         tns.ret_days_act                  AS ret_days_act,\n         tns.ret_vers_act                  AS ret_vers_act,\n         tns.ret_days_del                  AS ret_days_del,\n         tns.ret_vers_del                  AS ret_vers_del,\n         tns.ret_days_arc                  AS ret_days_arc,\n         tmp_events.job_result_code        AS job_result_code,\n         ptng.nodegroup_name               AS nodegroup_name,\n         (case when ptard.copy_technic is null then '' else ptard.copy_technic end)  AS copy_technic,\n         ptard.copy_bytes                  AS copy_bytes,\n         ptard.at_risk_bytes               AS at_risk_bytes\n      FROM  private_tsm_nodes  AS ptn\n      LEFT OUTER JOIN\n          (select server_id, node_id, coalesce(string_agg(result::text, ','),' ') as job_result_code from tsm_events_materialized where scheduled_start > ((current_date - interval '1 day') + time '0600' )  group by server_id, node_id ) as tmp_events \n          ON ( ptn.server_id = tmp_events.server_id and ptn.node_id = tmp_events.node_id )\n      LEFT OUTER JOIN tsm_node_summary  AS tns\n          ON ( ptn.server_id = tns.server_id and ptn.node_id = tns.node_id )\n      LEFT OUTER JOIN (select server_id, coalesce(string_agg(nodegroup_name, ','), ' ') as nodegroup_name, node_name from private_tsm_nodegroup group by server_id,node_name ) as ptng \n            ON ( ptng.server_id = ptn.server_id and ptng.node_name = ptn.node_name)\n      LEFT OUTER JOIN private_tsm_at_risk_data ptard ON ( ptard.server_id = ptn.server_id and ptard.node_id = ptn.node_id ) WHERE ptn.server_name=?	\N	\N	\N	\N	\N	\N	\N	server_name	\N		\N	f	f
