tsm_node_retention_policy	SG::Datasource::DBI	INSERT INTO tsm_node_retention_policy\n      SELECT\n              N.server_name   AS server_name,\n              N.server_id     AS server_id,\n              N.node_name     AS node_name,\n              N.node_id       AS node_id,\n              BU.retextra     AS ret_days_act,\n              BU.verexists    AS ret_vers_act,\n              BU.retonly      AS ret_days_del,\n              BU.verdeleted   AS ret_vers_del,\n              AR.retver       AS ret_days_arc\n      FROM      private_tsm_nodes     AS N\n      JOIN private_tsm_domain AS D ON ( D.server_id = N.server_id AND D.domain_name   = N.domain_name )\n      JOIN private_tsm_mgmtclasses AS M   ON ( M.server_id = N.server_id AND M.domain_name = D.domain_name AND M.class_name = D.defmgmtclass )\n      JOIN private_tsm_bu_copygroup  AS BU  ON ( BU.server_id = M.server_id AND BU.domain_name = M.domain_name AND BU.class_name = M.class_name AND BU.set_name = M.set_name ) \n      LEFT OUTER JOIN private_tsm_ar_copygroup  AS AR  ON ( AR.server_id = BU.server_id AND AR.domain_name = BU.domain_name AND AR.class_name = BU.class_name AND AR.set_name = BU.set_name )\n      WHERE M.set_name = 'ACTIVE' AND N.server_name=? ORDER BY N.node_id \n      ;	\N	\N	\N	\N	\N	\N	\N	server_name	\N		\N	f	f
