tsm_node_summary	SG::Datasource::DBI	INSERT INTO tsm_node_summary\n      SELECT\n          ptn.server_name                 AS server_name,\n          ptn.server_id                   AS server_id,\n          ptn.node_name                   AS node_name,\n          ptn.node_id                     AS node_id,\n          now()                           AS lastmod,\n          tnm.bkp_stat                    AS bkp_stat,\n          tnm.bkp_stat_name               AS node_bkp_stat_name,\n          tnm.days_since_last_completed   AS days_since_last_completed,\n          tnm.bkp_last_completed          AS bkp_last_completed,\n          tnm.bkp_last_source             AS bkp_last_source,\n          tnm.bkp_stat_timestamp          AS bkp_stat_timestamp,\n          NULL                            AS arc_stat,\n          NULL                            AS arc_stat_timestamp,\n          tnfs.capacity                   AS capacity,\n          tnfs.cap_used                   AS cap_used,\n          tnfs.pct_full                   AS pct_full,\n          tnmhf.hog_factor                AS hog_factor,\n          (case when tnfs.cap_used = 0 then 0 else (tnss.ses_size * 100) / (tnfs.cap_used) end)     AS pct_chg,\n          gr.stg_cur - gr.stg_past        AS stg_pri_gr,\n          gr.num_cur - gr.num_past        AS num_files_gr,\n          tno.stg                         AS stg,\n          tno.stg_pri                     AS stg_pri,\n          tno.stg_cpy                     AS stg_cpy,\n          tno.stg_diff                    AS stg_diff,\n          tno.stg_bak                     AS stg_bak,\n          tno.stg_bak_pri                 AS stg_bak_pri,\n          tno.stg_bak_cpy                 AS stg_bak_cpy,\n          tno.stg_arc                     AS stg_arc,\n          tno.stg_arc_pri                 AS stg_arc_pri,\n          tno.stg_arc_cpy                 AS stg_arc_cpy,\n          tno.stg_hsm                     AS stg_hsm,\n          tno.stg_hsm_pri                 AS stg_hsm_pri,\n          tno.stg_hsm_cpy                 AS stg_hsm_cpy,\n          tno.num_files                   AS num_files,\n          tnss.idlewait                   AS idlewait,\n          (case when tnss.ses_time = 0 then 0 else tnss.idlewait / tnss.ses_time * 100 end)     AS idlewait_pct,\n          tnss.idlewait                   AS idlewait_duration,\n          tnss.commwait                   AS commwait,\n          (case when tnss.ses_time = 0 then 0 else tnss.commwait / tnss.ses_time * 100 end)     AS commwait_pct,\n          tnss.commwait                   AS commwait_duration,\n          tnss.mediawait                  AS mediawait,\n          (case when tnss.ses_time = 0 then 0 else tnss.mediawait / tnss.ses_time * 100 end)     AS mediawait_pct,\n          tnss.mediawait                  AS mediawait_duration,\n          tnss.ses_time                   AS ses_time,\n          tnss.ses_size                   AS ses_size,\n          tnss.ses_rate                   AS ses_rate,\n          ptnsd.insp                      AS insp,\n          ptnsd.bkp                       AS bkp,\n          ptnsd.fail                      AS fail,\n          NULL                            AS res,\n          ptnsd.arc                       AS arc,\n          NULL                            AS ret,\n          ptnsd.upd                       AS upd,\n          ptnsd.reb                       AS reb,\n          ptnsd.del                       AS del,\n          ptnsd.exp                       AS exp,\n          ptnsd.dup                       AS dup,\n          ptnsd.bytes_tran                AS bytes_tran,\n          ptnsd.bytes_insp                AS bytes_insp,\n          ptnsd.reduct_ratio              AS reduct_ratio,\n          NULL                            AS ignored,\n          tnrp.ret_days_act               AS ret_days_act,\n          tnrp.ret_vers_act               AS ret_vers_act,\n          tnrp.ret_days_del               AS ret_days_del,\n          tnrp.ret_vers_del               AS ret_vers_del,\n          tnrp.ret_days_arc               AS ret_days_arc\n      FROM  private_tsm_nodes   AS ptn           \n      LEFT OUTER JOIN tsm_nodes_materialized      AS tnm   USING (server_id,node_id)\n      LEFT OUTER JOIN tsm_node_filespace_summary  AS tnfs  USING (server_id,node_id)\n      LEFT OUTER JOIN tsm_node_ms_hog_factor      AS tnmhf USING (server_id,node_id)\n      LEFT OUTER JOIN tsm_node_occupancy          AS tno   USING (server_id,node_id)\n      LEFT OUTER JOIN tsm_node_session_summary    AS tnss  USING (server_id,node_id)\n      LEFT OUTER JOIN \n        (SELECT server_id,node_id,tno.stg_cur,tno.num_cur,(CASE WHEN tnh_default.stg_past IS NOT NULL THEN tnh_default.stg_past ELSE tnh_min.stg_min END) as stg_past, (CASE WHEN tnh_default.num_past IS NOT NULL THEN tnh_default.num_past ELSE tnh_min.num_min END) as num_past\n         FROM\n         (SELECT server_id, node_id , SUM(stg_pri) as stg_cur, SUM(num_files)AS num_cur from tsm_node_occupancy GROUP BY server_id, node_id) AS tno\n         LEFT JOIN ( SELECT server_id, node_id , SUM(CASE WHEN date=(CURDATE() - make_interval(days=>?)) THEN stg_pri ELSE NULL END) as stg_past, SUM(CASE WHEN date=(CURDATE() - make_interval(days=>?) ) THEN num_files ELSE NULL END ) AS num_past FROM tsm_node_history WHERE date >= CURDATE() - make_interval(days=>?) GROUP BY server_id, node_id\n         ) AS tnh_default USING (server_id,node_id)\n         LEFT JOIN\n         ( SELECT his.server_id, his.node_id , SUM(stg_pri) as stg_min, SUM(num_files) AS num_min FROM tsm_node_history his join\n          ( select server_id, min(date)  as date from tsm_node_history where date<> curdate() group by 1 ) AS min\n          on (his.server_id=min.server_id and his.date=min.date) GROUP BY his.server_id, his.node_id\n         ) as tnh_min USING (server_id,node_id)\n        ) AS gr USING (server_id,node_id)\n      LEFT OUTER JOIN \n        (\n        SELECT server_id, node_id, SUM(backedup) AS bkp, SUM(inspected) AS insp, SUM(failed) AS fail, 0 AS res , SUM(archived) AS arc, 0 AS ret , SUM(updated) AS upd, SUM(rebound) AS reb, SUM(deleted) AS del, SUM(expired) AS exp , SUM(deduplicated) AS dup , SUM(bytes_inspected) AS bytes_insp , SUM(bytes_transferred) AS bytes_tran, AVG(reduction_ratio) AS reduct_ratio FROM private_tsm_node_session_details WHERE session_end >= ( NOW() - INTERVAL '1 DAY' ) GROUP BY server_id, node_id\n        )  AS ptnsd USING (server_id,node_id)\n      LEFT OUTER JOIN tsm_node_retention_policy   AS tnrp  USING (server_id,node_id) where ptn.server_name=?	\N	\N	\N	\N	\N	\N	\N	SGCONFIG_GRRATEDAYS SGCONFIG_GRRATEDAYS SGCONFIG_GRRATEDAYS server_name	\N		\N	f	f
update_tsm_server_libinfo	SG::Datasource::DBI	UPDATE tsm_servers_materialized tsm\n      SET library_slots = tsls.slots,\n          library_tapes = tsls.tapes,\n          library_mounts_per_day = tsls.mounts_per_day,\n          library_scratch = tsls.scratch,\n          library_pctfull = tsls.pctfull\n      FROM tsm_server_library_slots  AS tsls  WHERE tsm.server_id = tsls.server_id;	\N	\N	\N	\N	\N	\N	\N		\N		\N	f	f
update_tsm_server_status	SG::Datasource::DBI	UPDATE Servers tsm\n      SET os_type = lower(ptss.platform),\n          sw_version =  ptss.version||'.'||(case when ptss.release>0 then ptss.release else 0 end)||'.'||(case when ptss.level>0 then ptss.level else 0 end)\n      FROM private_tsm_server_status  AS ptss  WHERE tsm.id = ptss.server_id;	\N	\N	\N	\N	\N	\N	\N		\N		\N	f	f
tsm_server_library_slots	SG::Datasource::DBI	INSERT INTO tsm_server_library_slots\nSELECT\n  srv.server_name AS server_name,\n  srv.server_id AS server_id,\n  (case when SUM(tlc.slots_count) > 0 then SUM(tlc.slots_count) else 0 end) AS slots,\n  lib.tapes AS tapes,\n  (case when tmpd.mounts_per_day > 0 then tmpd.mounts_per_day else 0 end) AS mounts_per_day,\n  lib.scratch_tot,\n  (case when SUM(tlc.slots_count) > 0 then lib.tapes/SUM(tlc.slots_count)*100 else 0 end) AS pctfull\nFROM\n    ( SELECT server.name as server_name,\n             server.id as server_id\n           FROM   Symbols AS server,\n                  Servers AS s\n           WHERE server.id = s.id\n           AND server.name != 'site'\n    ) as srv\n    LEFT OUTER JOIN tsm_libslots_conf AS tlc\n    ON ( tlc.server_name  = srv.server_name )\n    LEFT OUTER JOIN (\n        SELECT server_id , COUNT(mount_start) AS mounts_per_day \n        FROM tsm_mnt_info \n        WHERE mount_start >  ( NOW() - INTERVAL '1 DAY' )\n          AND mount_type='tape'\n        GROUP BY server_id ) AS tmpd\n    ON ( srv.server_id = tmpd.server_id)\n    LEFT OUTER JOIN  (\n        SELECT server_id  AS server_id,\n               SUM(scratch_tapes) AS scratch_tot,\n               SUM(tapes ) AS tapes\n        FROM tsm_libtapes_info\n        GROUP BY server_id ) as lib\n    ON ( srv.server_id = lib.server_id )\n    WHERE lib.tapes is not null\n    GROUP BY srv.server_name, srv.server_id, lib.tapes, tmpd.mounts_per_day, lib.scratch_tot\n      ;	\N	\N	\N	\N	\N	\N	\N		\N		\N	f	t
