tsm_node_stgpool_occupancy	SG::Datasource::DBI	INSERT INTO tsm_node_stgpool_occupancy\n       SELECT ptn.server_name                                                                                       AS server_name,\n          ptn.server_id                                                                                         AS server_id,\n          ptn.node_name                                                                                         AS node_name,\n          ptn.node_id                                                                                           AS node_id,\n          pto.stgpool_name                                                                                      AS stgpool_name,\n          SUM(CASE WHEN type = 'Arch' THEN logical_mb * 1048576 ELSE 0 END)                                     AS stg_arc,\n          SUM(CASE WHEN type = 'Arch' and pooltype = 'PRIMARY' THEN logical_mb * 1048576 ELSE 0 END)           AS stg_arc_pri,\n          SUM(CASE WHEN type = 'Arch' and pooltype = 'COPY' THEN logical_mb * 1048576 ELSE 0 END)              AS stg_arc_cpy,\n          SUM(CASE WHEN type = 'Bkup' THEN  logical_mb * 1048576 ELSE 0 END)                                   AS stg_bak,\n          SUM(CASE WHEN type = 'Bkup' and pooltype = 'PRIMARY' THEN logical_mb * 1048576 ELSE 0 END)           AS stg_bak_pri,\n          SUM(CASE WHEN type = 'Bkup' and pooltype = 'COPY' THEN logical_mb * 1048576 ELSE 0 END)              AS stg_bak_cpy,\n          SUM(CASE WHEN type = 'Spmg' THEN  logical_mb * 1048576 ELSE 0 END)                                   AS stg_hsm,\n          SUM(CASE WHEN type = 'Spmg' and pooltype = 'PRIMARY' THEN  logical_mb * 1048576 ELSE 0 END)          AS stg_hsm_pri,\n          SUM(CASE WHEN type = 'Spmg' and pooltype = 'COPY' THEN  logical_mb * 1048576 ELSE 0 END)             AS stg_hsm_cpy,\n          SUM(CASE WHEN pooltype = 'COPY' THEN  logical_mb * 1048576 ELSE 0 END)                               AS stg_cpy,\n          SUM(CASE WHEN pooltype = 'PRIMARY' THEN  logical_mb * 1048576 ELSE 0 END)                            AS stg_pri,\n          SUM( CASE WHEN filespace_name = 'DELETED' \n                    THEN 0 \n                    ELSE CASE WHEN pooltype = 'PRIMARY' AND toip.stgpool_name is NULL \n                              THEN CASE WHEN logical_mb =0 THEN reporting_mb * 1048576 ELSE  logical_mb * 1048576  END\n                              ELSE CASE WHEN pooltype = 'COPY' THEN (CASE WHEN logical_mb =0 THEN reporting_mb ELSE logical_mb  * -1048576 END) ELSE 0  END\n                              END\n                    END\n          ) as stg_diff,\n          SUM(logical_mb * 1048576)                                                                             AS stg,\n          SUM(num_files)                                                                                        AS num_files\n      FROM                 private_tsm_nodes     AS ptn\n      LEFT OUTER JOIN private_tsm_occupancy AS pto ON ( ptn.node_id = pto.node_id )\n      LEFT OUTER JOIN private_tsm_stgpools  AS pts ON (      pto.server_id    = pts.server_id\n                                                              AND pto.stgpool_name = pts.stgpool_name )\n      LEFT OUTER JOIN tsm_offsite_ignored_pools  AS toip ON (      toip.server_id    = pts.server_id\n                                                              AND toip.stgpool_name = pts.stgpool_name )\n      WHERE ptn.server_name=?\n      GROUP BY ptn.server_id,\n              ptn.server_name,\n              ptn.node_id,\n              ptn.node_name,\n              pto.stgpool_name	\N	\N	\N	\N	\N	\N	\N	server_name	\N		\N	t	f
tsm_node_occupancy	SG::Datasource::DBI	INSERT INTO tsm_node_occupancy\n          SELECT tnso.server_name       AS server_name,\n                 tnso.server_id         AS server_id,\n                 tnso.node_name         AS node_name,\n                 tnso.node_id           AS node_id,\n                 SUM(tnso.stg_arc)      AS stg_arc,\n                 SUM(tnso.stg_arc_pri)  AS stg_arc_pri,\n                 SUM(tnso.stg_arc_cpy)  AS stg_arc_cpy,\n                 SUM(tnso.stg_bak)      AS stg_bak,\n                 SUM(tnso.stg_bak_pri)  AS stg_bak_pri,\n                 SUM(tnso.stg_bak_cpy)  AS stg_bak_cpy,\n                 SUM(tnso.stg_hsm)      AS stg_hsm,\n                 SUM(tnso.stg_hsm_pri)  AS stg_hsm_pri,\n                 SUM(tnso.stg_hsm_cpy)  AS stg_hsm_cpy,\n                 SUM(tnso.stg_cpy)      AS stg_cpy,\n                 SUM(tnso.stg_pri)      AS stg_pri,\n                 SUM(tnso.stg_diff)     AS stg_diff,\n                 SUM(tnso.stg)          AS stg,\n                 SUM(tnso.num_files)    AS num_files\n          FROM tsm_node_stgpool_occupancy tnso\n          WHERE server_name=?\n          GROUP BY tnso.server_id,\n                        tnso.server_name,\n                        tnso.node_id,\n                        tnso.node_name	\N	\N	\N	\N	\N	\N	\N	server_name	\N		\N	t	f
tsm_server_occupancy	SG::Datasource::DBI	INSERT INTO tsm_server_occupancy\n        SELECT tno.server_name       AS server_name,\n               tno.server_id         AS server_id,\n               SUM(tno.stg_arc)      AS stg_arc,\n               SUM(tno.stg_arc_pri)  AS stg_arc_pri,\n               SUM(tno.stg_arc_cpy)  AS stg_arc_cpy,\n               SUM(tno.stg_bak)      AS stg_bak,\n               SUM(tno.stg_bak_pri)  AS stg_bak_pri,\n               SUM(tno.stg_bak_cpy)  AS stg_bak_cpy,\n               SUM(tno.stg_hsm)      AS stg_hsm,\n               SUM(tno.stg_hsm_pri)  AS stg_hsm_pri,\n               SUM(tno.stg_hsm_cpy)  AS stg_hsm_cpy,\n               SUM(tno.stg_cpy)      AS stg_cpy,\n               SUM(tno.stg_pri)      AS stg_pri,\n               SUM(tno.stg_diff)     AS stg_diff,\n               SUM(tno.stg)          AS stg,\n               SUM(tno.num_files)    AS num_files\n        FROM tsm_node_occupancy tno\n        WHERE server_name=?\n        GROUP BY tno.server_id, tno.server_name	\N	\N	\N	\N	\N	\N	\N	server_name	\N		\N	t	f
