tsm_node_filespace_summary	SG::Datasource::DBI	INSERT INTO tsm_node_filespace_summary\n          SELECT tfs.server_name    AS server_name,\n               tfs.server_id      AS server_id,\n               tfs.node_name      AS node_name,\n               tfs.node_id        AS node_id,\n               sum(tfs.capacity)  AS capacity,\n               (CASE when sum(tfs.capacity) = 0 then 0 else sum(tfs.cap_used)/sum(tfs.capacity)*100 end)  AS pct_full,\n               sum(tfs.cap_used)  AS cap_used\n          FROM tsm_filespace_summary tfs\n          WHERE server_name=?\n          GROUP by server_id,\n                 node_id,\n                 server_name,\n                 node_name	\N	\N	\N	\N	\N	\N	\N	server_name	\N		\N	t	f
tsm_server_filespace_summary	SG::Datasource::DBI	INSERT INTO tsm_server_filespace_summary\n        SELECT server_info.server_name       AS server_name,\n                      server_info.server_id         AS server_id,\n                      server_info.num_nodes         AS nodes,\n                      server_capacity_summary.cap   AS cap,\n                      server_capacity_summary.used  AS used\n        FROM ( SELECT server_name     AS server_name,\n                      server_id       AS server_id,\n                      count(node_id)  AS  num_nodes\n               FROM private_tsm_nodes group by server_name,server_id\n               )  server_info\n        LEFT OUTER JOIN\n             ( SELECT server_id             AS server_id,\n                       SUM(tnfs.capacity)  AS cap,\n                       SUM(tnfs.cap_used)  AS used\n               FROM tsm_node_filespace_summary tnfs group by server_id\n               ) server_capacity_summary\n         ON server_info.server_id = server_capacity_summary.server_id where server_info.server_name=?	\N	\N	\N	\N	\N	\N	\N	server_name	\N		\N	t	f
tsm_node_ms_hog_factor	SG::Datasource::DBI	INSERT INTO tsm_node_ms_hog_factor\n          SELECT tno.server_name                                        AS server_name,\n                 tno.server_id                                          AS server_id,\n                 tno.node_name                                          AS node_name,\n                 tno.node_id                                            AS node_id,\n                 CASE WHEN tnfs.cap_used = 0 THEN  0 ELSE tno.stg_pri / tnfs.cap_used END AS hog_factor\n          FROM tsm_node_occupancy         tno,\n               tsm_node_filespace_summary tnfs\n          WHERE tno.server_id =  tnfs.server_id\n            AND tno.node_id   = tnfs.node_id AND tno.server_name=?	\N	\N	\N	\N	\N	\N	\N	server_name	\N		\N	t	f
tsm_storage_materialized	SG::Datasource::DBI	INSERT INTO tsm_storage_materialized\n        SELECT pto.server_name                             AS server_name,\n               pto.server_id                               AS server_id,\n               pto.node_name                               AS node_name,\n               pto.node_id                                 AS node_id,\n               pts.stgpool_name                            AS stgpool_name,\n               ptf.filespace_name                          AS filespace_name,\n               ptf.filespace_id                            AS filespace_id,\n               pto.type                                    AS type,\n               pto.num_files                               AS num_files,\n               (pto.physical_mb * 1048576)                 AS physical_bytes,\n               (pto.logical_mb * 1048576)                  AS logical_bytes,\n               (pto.reporting_mb * 1048576)                AS reporting_bytes,\n               tsi.type                                    AS server_swtype,\n               tsi.sw_version                              AS server_swversion,\n               tsi.os_type                                 AS server_ostype,\n               ptn.ostype                                  AS node_ostype,\n               ptn.osversion                               AS node_osversion,\n               ptn.swversion                               AS node_swversion,\n               ptn.domain_name                             AS node_domain_name,\n               ptn.locked                                  AS node_locked,\n               ptn.contact                                 AS node_contact,\n               ptn.tcp_address                             AS node_tcp_address,\n               ptn.tcp_name                                AS node_tcp_name,\n               pts.pooltype                                AS stgpool_pooltype,\n               pts.devclass                                AS stgpool_devclass,\n               pts.access                                  AS stgpool_access,\n               pts.collocate                               AS stgpool_collocate,\n               pts.cache                                   AS stgpool_cache,\n               pts.nextstgpool                             AS stgpool_nextpool,\n               pts.deduplicate                             AS stgpool_deduplicate,\n               pts.identifyprocess                         AS stgpool_identifyprocess,\n               ROUND(pts.space_saved_bytes/1024/1024,2)    AS stgpool_space_saved_mb,\n               pts.client_dedup_data                       AS stgpool_client_dedup_data,\n               pts.est_capacity                            AS stgpool_est_capacity,\n               pts.trigger_pct_util                        AS stgpool_trigger_pct_util,\n               pts.pct_utilized                            AS stgpool_pct_utilized,\n               pts.pct_migr                                AS stgpool_pct_migr,\n               pts.pct_logical                             AS stgpool_pct_logical,\n               ptd.devtype                                 AS stgpool_devtype,\n               pts.maxscratch                              AS stgpool_maxscratch,\n               pts.numscratchused                          AS stgpool_numscratchused,\n               ptf.backup_start                            AS filespace_backup_start,\n               ptf.backup_end                              AS filespace_backup_end,\n               ptf.filespace_type                          AS filespace_type,\n               ptf.capacity                                AS filespace_capacity,\n               ptf.pct_util                                AS filespace_pct_util,\n               ((ptf.capacity * ptf.pct_util) / 100)   AS filespace_cap_used\n          FROM      private_tsm_occupancy   AS pto\n               JOIN private_tsm_nodes       AS ptn ON (     pto.server_id    = ptn.server_id\n                                                            AND pto.node_id      = ptn.node_id\n                                                          )\n               JOIN private_tsm_stgpools    AS pts ON (     pto.server_id    = pts.server_id\n                                                            AND pto.stgpool_name = pts.stgpool_name\n                                                          )\n               LEFT JOIN private_tsm_devclasses  AS ptd ON (     pts.server_id    = ptd.server_id\n                                                            AND pts.devclass     = ptd.devclass_name\n                                                          )\n               JOIN private_tsm_filespaces  AS ptf ON (     pto.server_id    = ptf.server_id\n                                                            AND pto.node_id      = ptf.node_id\n                                                            AND pto.filespace_id = ptf.filespace_id\n                                                          )\n               JOIN Servers         AS tsi ON ( pto.server_id        = tsi.id ) where pto.server_name=?	\N	\N	\N	\N	\N	\N	\N	server_name	\N		\N	t	f
