CREATE RULE "_RETURN" AS
    ON SELECT TO backupclientdaily_view DO INSTEAD  SELECT cl.name AS node_name,
    cd.node_id,
    cd.server_id,
    cd.stg_pri_gr,
    cd.stg_pri,
    cd.ses_size,
    cd.ses_time,
    cd.ses_rate,
    cd.bkp,
    cd.num_files,
    cd.num_files_gr,
    cd.capacity_disk_mb,
    cd.capacity_tape_mb,
    cd.hog_factor,
    COALESCE(cl.ostype, ''::character varying) AS platform,
    user_sym.id AS user_id,
    user_sym.name AS user_name
   FROM (((( SELECT backupclientdaily.backupclientid AS node_id,
            backupclientdaily.backupserverid AS server_id,
            backupclientdaily.retentiongrouth AS stg_pri_gr,
            backupclientdaily.retentionmb AS stg_pri,
            sum((backupclientdaily.backupcapacityfullmb + backupclientdaily.backupcapacityincmb)) AS ses_size,
            backupclientdaily.durationseconds AS ses_time,
            backupclientdaily.transferratembs AS ses_rate,
            backupclientdaily.numberoffiles AS bkp,
            backupclientdaily.num_files,
            backupclientdaily.num_files_gr,
            (backupclientdaily.capacity_disk_kb / (1024)::numeric) AS capacity_disk_mb,
            (backupclientdaily.capacity_tape_kb / (1024)::numeric) AS capacity_tape_mb,
            backupclientdaily.hog_factor
           FROM backupclientdaily
          GROUP BY backupclientdaily.backupclientid) cd
     LEFT JOIN backupclient cl ON ((cd.node_id = (cl.backupclientid)::numeric)))
     JOIN user_viewable_symbols uvs ON ((cd.node_id = (uvs.symbol_id)::numeric)))
     JOIN symbols user_sym ON ((uvs.user_id = user_sym.id)));


